    flex: 1;
    overflow-y: auto;
    padding: var(--space-lg);
    background: var(--bg-primary);
}

.page {
    display: none;
}

.page.active {
    display: block;
}

/* ‰ª™Ë°®ÊùøÁΩëÊ†ºÂ∏ÉÂ±Ä */
.dashboard-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
    gap: var(--space-lg);
}

/* Âç°ÁâáÊ†∑Âºè */
.card {
    background: var(--bg-secondary);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-md);
    overflow: hidden;
}

.card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--space-md);
    border-bottom: 1px solid var(--border-primary);
}

.card-header h3 {
    font-size: 16px;
    font-weight: 500;
    color: var(--text-primary);
}

.card-body {
    padding: var(--space-md);
}

/* Á≥ªÁªüÁä∂ÊÄÅÂç°Áâá */
.status-item {
    display: flex;
    justify-content: space-between;
    padding: var(--space-sm) 0;
    border-bottom: 1px solid var(--border-primary);
}

.status-item:last-child {
    border-bottom: none;
}

.status-item .label {
    color: var(--text-tertiary);
    font-size: 14px;
}

.status-item .value {
    color: var(--text-primary);
    font-size: 14px;
    font-weight: 500;
}

/* Â∏ÇÂú∫ÁªüËÆ° */
.market-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: var(--space-md);
}

.stat-item {
    text-align: center;
}

.stat-label {
    color: var(--text-tertiary);
    font-size: 12px;
    margin-bottom: var(--space-xs);
}

.stat-value {
    color: var(--text-primary);
    font-size: 20px;
    font-weight: 600;
    font-family: 'Roboto Mono', monospace;
}

.stat-change {
    font-size: 14px;
    margin-top: var(--space-xs);
}

.stat-change.positive {
    color: var(--bull-color);
}

.stat-change.negative {
    color: var(--bear-color);
}

/* ÂõæË°®ÂÆπÂô® */
.chart-container {
    grid-column: span 2;
    min-height: 400px;
}

.chart {
    width: 100%;
    height: 350px;
}

.chart-controls {
    display: flex;
    gap: var(--space-sm);
}

.chart-controls select {
    padding: var(--space-xs) var(--space-sm);
    background: var(--bg-tertiary);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-sm);
    color: var(--text-primary);
    font-size: 12px;
}

/* ÊåâÈíÆÊ†∑Âºè */
.btn-primary, .btn-secondary {
    padding: var(--space-sm) var(--space-md);
    border: none;
    border-radius: var(--radius-sm);
    cursor: pointer;
    font-size: 14px;
    font-weight: 500;
    transition: all 0.2s ease;
}

.btn-primary {
    background: var(--primary-color);
    color: white;
}

.btn-primary:hover {
    background: var(--primary-hover);
    transform: translateY(-1px);
}

.btn-secondary {
    background: transparent;
    color: var(--text-secondary);
    border: 1px solid var(--border-primary);
}

.btn-secondary:hover {
    background: var(--bg-tertiary);
    border-color: var(--primary-color);
    color: var(--primary-color);
}

/* Ë°®ÂçïÊ†∑Âºè */
.form-group {
    margin-bottom: var(--space-md);
}

.form-group label {
    display: block;
    margin-bottom: var(--space-xs);
    color: var(--text-secondary);
    font-size: 12px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: var(--space-sm) var(--space-md);
    background: var(--bg-tertiary);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-sm);
    color: var(--text-primary);
    font-size: 14px;
    transition: all 0.2s ease;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--primary-color);
    background: var(--bg-quaternary);
}

.form-group textarea {
    resize: vertical;
    min-height: 80px;
    font-family: 'Roboto Mono', monospace;
    font-size: 13px;
}

.form-row {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: var(--space-md);
}

/* Êï∞ÊçÆË°®Ê†º */
.data-table {
    width: 100%;
    border-collapse: collapse;
}

.data-table thead th {
    text-align: left;
    padding: var(--space-sm);
    color: var(--text-secondary);
    font-size: 12px;
    text-transform: uppercase;
    border-bottom: 1px solid var(--border-primary);
}

.data-table tbody td {
    padding: var(--space-sm);
    color: var(--text-primary);
    font-size: 14px;
    border-bottom: 1px solid var(--border-primary);
}

.data-table tbody tr:hover {
    background: var(--bg-tertiary);
}

/* ÊêúÁ¥¢ËæìÂÖ•Ê°Ü */
.search-input {
    padding: var(--space-sm) var(--space-md);
    background: var(--bg-tertiary);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-sm);
    color: var(--text-primary);
    width: 300px;
}

/* Â∑•ÂÖ∑Ê†è */
.market-toolbar,
.strategy-toolbar {
    display: flex;
    gap: var(--space-md);
    margin-bottom: var(--space-lg);
}

/* Á≠ñÁï•ÂíåAIÈ°µÈù¢Â∏ÉÂ±Ä */
.strategy-container,
.ai-container,
.backtest-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(500px, 1fr));
    gap: var(--space-lg);
}

.full-width {
    grid-column: 1 / -1;
}

/* Â∫ïÈÉ®Áä∂ÊÄÅÊ†è */
.footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 var(--space-lg);
    height: 32px;
    background: var(--bg-secondary);
    border-top: 1px solid var(--border-primary);
    font-size: 12px;
    color: var(--text-tertiary);
}

.footer-left,
.footer-right {
    display: flex;
    align-items: center;
    gap: var(--space-md);
}

.separator {
    color: var(--border-primary);
}

/* Toast Ê∂àÊÅØÊèêÁ§∫ */
.toast-container {
    position: fixed;
    top: 20px;
    right: 20px;
    z-index: 1000;
}

.toast {
    background: var(--bg-tertiary);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-sm);
    padding: var(--space-md);
    margin-bottom: var(--space-sm);
    min-width: 300px;
    animation: slideIn 0.3s ease;
    box-shadow: var(--shadow-lg);
}

.toast.success {
    border-left: 3px solid var(--success-color);
}

.toast.error {
    border-left: 3px solid var(--error-color);
}

.toast.warning {
    border-left: 3px solid var(--warning-color);
}

.toast.info {
    border-left: 3px solid var(--info-color);
}

@keyframes slideIn {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

/* Êó†Êï∞ÊçÆÊèêÁ§∫ */
.no-data {
    text-align: center;
    color: var(--text-tertiary);
    padding: var(--space-xl);
    font-size: 14px;
}

/* Âà∑Êñ∞ÊåâÈíÆ */
.refresh-btn {
    cursor: pointer;
    font-size: 16px;
    transition: transform 0.3s ease;
}

.refresh-btn:hover {
    transform: rotate(180deg);
}

/* ÂìçÂ∫îÂºèËÆæËÆ° */
@media (max-width: 1200px) {
    .dashboard-grid {
        grid-template-columns: 1fr;
    }

    .chart-container {
        grid-column: span 1;
    }
}

@media (max-width: 768px) {
    .header {
        padding: 0 var(--space-md);
    }

    .nav-menu {
        display: none;
    }

    .api-config {
        flex-direction: column;
    }

    #api-url {
        width: 150px;
    }

    .main-content {
        padding: var(--space-md);
    }

    .strategy-container,
    .ai-container,
    .backtest-container {
        grid-template-columns: 1fr;
    }
}

/* Âä†ËΩΩÂä®Áîª */
.loading {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 2px solid var(--border-primary);
    border-top-color: var(--primary-color);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Á≠ñÁï•ÂàóË°®Ê†∑Âºè */
.strategies-list,
.performance-list {
    display: flex;
    flex-direction: column;
    gap: var(--space-sm);
}

.strategy-item,
.performance-item {
    padding: var(--space-md);
    background: var(--bg-tertiary);
    border-radius: var(--radius-sm);
    border: 1px solid var(--border-primary);
    transition: all 0.2s ease;
}

.strategy-item:hover,
.performance-item:hover {
    border-color: var(--primary-color);
    transform: translateX(2px);
}

.strategy-item-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--space-sm);
}

.strategy-name {
    font-weight: 500;
    color: var(--text-primary);
}

.strategy-status {
    padding: 2px 8px;
    border-radius: var(--radius-sm);
    font-size: 12px;
}

.strategy-status.running {
    background: rgba(16, 185, 129, 0.2);
    color: var(--success-color);
}

.strategy-status.stopped {
    background: rgba(139, 146, 167, 0.2);
    color: var(--text-tertiary);
}

/* AIÁªìÊûúÊ†∑Âºè */
.ai-results {
    max-height: 500px;
    overflow-y: auto;
}

.ai-result-item {
    padding: var(--space-md);
    background: var(--bg-tertiary);
    border-radius: var(--radius-sm);
    margin-bottom: var(--space-md);
}

.ai-result-header {
    display: flex;
    justify-content: space-between;
    margin-bottom: var(--space-sm);
}

.ai-confidence {
    display: flex;
    align-items: center;
    gap: var(--space-xs);
    font-size: 12px;
    color: var(--text-secondary);
}

.confidence-bar {
    width: 60px;
    height: 4px;
    background: var(--bg-quaternary);
    border-radius: 2px;
    overflow: hidden;
}

.confidence-fill {
    height: 100%;
    background: var(--primary-color);
    transition: width 0.3s ease;
}

/* ÂõûÊµãÁªìÊûúÊ†∑Âºè */
.backtest-results {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: var(--space-md);
}

.result-metric {
    padding: var(--space-md);
    background: var(--bg-tertiary);
    border-radius: var(--radius-sm);
    text-align: center;
}

.metric-label {
    color: var(--text-tertiary);
    font-size: 12px;
    text-transform: uppercase;
    margin-bottom: var(--space-xs);
}

.metric-value {
    color: var(--text-primary);
    font-size: 24px;
    font-weight: 600;
    font-family: 'Roboto Mono', monospace;
}

