function loadMockChartData() {
    if (!chart || !chart.candlestickSeries) return;

    const data = [];
    const volumeData = [];
    const basePrice = 43000;
    const now = Date.now() / 1000;

    for (let i = 0; i < 100; i++) {
        const time = now - (100 - i) * 300; // 5ÂàÜÈíüKÁ∫ø
        const open = basePrice + Math.random() * 1000 - 500;
        const close = open + Math.random() * 200 - 100;
        const high = Math.max(open, close) + Math.random() * 100;
        const low = Math.min(open, close) - Math.random() * 100;

        data.push({
            time: time,
            open: open,
            high: high,
            low: low,
            close: close
        });

        volumeData.push({
            time: time,
            value: Math.random() * 1000000,
            color: close >= open ? '#00C087' : '#FF3B30'
        });
    }

    chart.candlestickSeries.setData(data);
    chart.volumeSeries.setData(volumeData);
}

