// ÂàùÂßãÂåñÂ∫îÁî®
function initializeApp() {
    console.log('ÂàùÂßãÂåñÂ∫îÁî®...');

    // Âª∂ËøüÂàùÂßãÂåñÂõæË°®ÔºåÁ°Æ‰øùÂÆπÂô®Â≠òÂú®
    setTimeout(() => {
        try {
            initializeChart();
        } catch (error) {
            console.error('ÂõæË°®ÂàùÂßãÂåñÂ§±Ë¥•:', error);
        }
    }, 100);

    // ËÆæÁΩÆÈªòËÆ§Êó•Êúü
    const today = new Date().toISOString().split('T')[0];
    const startDate = new Date();
    startDate.setMonth(startDate.getMonth() - 1);

    const backtestStart = document.getElementById('backtest-start');
    const backtestEnd = document.getElementById('backtest-end');
    if (backtestStart) backtestStart.value = startDate.toISOString().split('T')[0];
    if (backtestEnd) backtestEnd.value = today;
}

// ËÆæÁΩÆ‰∫ã‰ª∂ÁõëÂê¨Âô®
function setupEventListeners() {
    // ÂØºËà™ÂàáÊç¢
    document.querySelectorAll('.nav-item').forEach(item => {
        item.addEventListener('click', (e) => {
            const page = e.target.dataset.page;
            switchPage(page);
        });
    });

    // ËøûÊé•ÊåâÈíÆ
    document.getElementById('connect-btn').addEventListener('click', connectToAPI);

    // Âà∑Êñ∞Á≥ªÁªüÁä∂ÊÄÅ
    document.querySelector('[data-action="refresh-status"]')?.addEventListener('click', checkSystemStatus);

    // Ëé∑ÂèñÂ∏ÇÂú∫Êï∞ÊçÆ
    document.getElementById('fetch-market-data')?.addEventListener('click', fetchMarketData);

    // Âä†ËΩΩÁ≠ñÁï•ÂàóË°®
    document.getElementById('load-strategies')?.addEventListener('click', loadStrategies);

    // ÂàõÂª∫Á≠ñÁï•
    document.getElementById('create-strategy')?.addEventListener('click', () => {
        showToast('info', 'Á≠ñÁï•ÂàõÂª∫ÂäüËÉΩÂºÄÂèë‰∏≠...');
    });

    // Á≠ñÁï•Ë°®ÂçïÊèê‰∫§
    document.getElementById('strategy-form')?.addEventListener('submit', (e) => {
        e.preventDefault();
        saveStrategy();
    });

    // ÂõûÊµãË°®ÂçïÊèê‰∫§
    document.getElementById('backtest-form')?.addEventListener('submit', (e) => {
        e.preventDefault();
        runBacktest();
    });

    // AIÂàÜÊûêË°®ÂçïÊèê‰∫§
    document.getElementById('ai-form')?.addEventListener('submit', (e) => {
        e.preventDefault();
        runAIAnalysis();
    });

    // ‰∫§ÊòìÂØπÈÄâÊã©ÂèòÂåñ
    document.getElementById('symbol-select')?.addEventListener('change', (e) => {
        updateChart(e.target.value);
    });

    // Êó∂Èó¥Âë®ÊúüÈÄâÊã©ÂèòÂåñ
    document.getElementById('timeframe-select')?.addEventListener('change', (e) => {
        updateChartTimeframe(e.target.value);
    });
}

// È°µÈù¢ÂàáÊç¢
function switchPage(page) {
    console.log('ÂàáÊç¢Âà∞È°µÈù¢:', page);

    // Êõ¥Êñ∞ÂØºËà™Áä∂ÊÄÅ
    document.querySelectorAll('.nav-item').forEach(item => {
        item.classList.remove('active');
        if (item.dataset.page === page) {
            item.classList.add('active');
        }
    });

    // ÂàáÊç¢È°µÈù¢ÊòæÁ§∫
    document.querySelectorAll('.page').forEach(p => {
        p.classList.remove('active');
    });

    const targetPage = document.getElementById(`page-${page}`);
    if (targetPage) {
        targetPage.classList.add('active');
        console.log(`È°µÈù¢ ${page} Â∑≤ÊøÄÊ¥ª`);
    } else {
        console.error(`È°µÈù¢ ${page} Êú™ÊâæÂà∞`);
    }

    currentPage = page;

    // È°µÈù¢ÁâπÂÆöÁöÑÂàùÂßãÂåñ - ÊØèÊ¨°ÂàáÊç¢ÈÉΩÂä†ËΩΩÊï∞ÊçÆ
    switch(page) {
        case 'dashboard':
            loadMockPrices();
            loadMockChartData();
            break;
        case 'market':
            loadMockMarketData();
            break;
        case 'strategy':
            loadMockStrategies();
            break;
        case 'backtest':
            loadMockStrategies(); // ‰∏∫ÂõûÊµãÈ°µÈù¢Âä†ËΩΩÁ≠ñÁï•ÈÄâÈ°π
            break;
        case 'ai':
            // AIÈ°µÈù¢ÂáÜÂ§á
            break;
    }
}

// ËøûÊé•Âà∞API
async function connectToAPI() {
    const url = document.getElementById('api-url').value;
    apiUrl = url;

    // Ê£ÄÊü•APIÂÅ•Â∫∑Áä∂ÊÄÅ
    try {
        const response = await fetch(`${apiUrl}/health`);
        if (response.ok) {
            updateConnectionStatus(true);
            showToast('success', 'APIËøûÊé•ÊàêÂäüÔºÅ');

            // Â∞ùËØïÂª∫Á´ãWebSocketËøûÊé•
            connectWebSocket();

            // Âä†ËΩΩÂàùÂßãÊï∞ÊçÆ
            checkSystemStatus();
        } else {
            throw new Error('APIËøûÊé•Â§±Ë¥•');
        }
    } catch (error) {
        updateConnectionStatus(false);

        if (mockMode) {
            showToast('warning', 'Êó†Ê≥ïËøûÊé•Âà∞APIÔºå‰ΩøÁî®Ê®°ÊãüÊï∞ÊçÆÊ®°Âºè');
            updateConnectionStatus(true);
            initializeMockWebSocket();
        } else {
            showToast('error', `ËøûÊé•Â§±Ë¥•: ${error.message}`);
        }
    }
}

// WebSocketËøûÊé•
function connectWebSocket() {
    const wsUrl = apiUrl.replace('http', 'ws') + '/ws';

    try {
        wsConnection = new WebSocket(wsUrl);

        wsConnection.onopen = () => {
            console.log('WebSocketËøûÊé•ÊàêÂäü');
            document.getElementById('websocket-status').textContent = 'Â∑≤ËøûÊé•';
            document.getElementById('websocket-status').style.color = 'var(--success-color)';
        };

        wsConnection.onmessage = (event) => {
            handleWebSocketMessage(JSON.parse(event.data));
        };

        wsConnection.onerror = (error) => {
            console.error('WebSocketÈîôËØØ:', error);
            document.getElementById('websocket-status').textContent = 'ËøûÊé•ÈîôËØØ';
            document.getElementById('websocket-status').style.color = 'var(--error-color)';
        };

        wsConnection.onclose = () => {
            console.log('WebSocketËøûÊé•ÂÖ≥Èó≠');
            document.getElementById('websocket-status').textContent = 'Â∑≤Êñ≠ÂºÄ';
            document.getElementById('websocket-status').style.color = 'var(--warning-color)';

            // 5ÁßíÂêéÂ∞ùËØïÈáçËøû
            setTimeout(() => {
                if (!wsConnection || wsConnection.readyState === WebSocket.CLOSED) {
                    connectWebSocket();
                }
            }, 5000);
        };
    } catch (error) {
        console.error('WebSocketËøûÊé•Â§±Ë¥•:', error);
        if (mockMode) {
            initializeMockWebSocket();
        }
    }
}

// Â§ÑÁêÜWebSocketÊ∂àÊÅØ
function handleWebSocketMessage(data) {
    switch (data.type) {
        case 'price_update':
            updatePriceDisplay(data.symbol, data.price, data.change);
            if (chart && data.symbol === document.getElementById('symbol-select')?.value) {
                updateChartRealtime(data);
            }
            break;
        case 'market_stats':
            updateMarketStats(data);
            break;
        case 'strategy_update':
            updateStrategyPerformance(data);
            break;
        default:
            console.log('Êî∂Âà∞WebSocketÊ∂àÊÅØ:', data);
    }

    // Êõ¥Êñ∞ÊúÄÂêéÊõ¥Êñ∞Êó∂Èó¥
    document.getElementById('data-update-time').textContent = 
        `ÊúÄÂêéÊõ¥Êñ∞: ${new Date().toLocaleTimeString()}`;
}

// Êõ¥Êñ∞ËøûÊé•Áä∂ÊÄÅ
function updateConnectionStatus(connected) {
    const indicator = document.getElementById('ws-status');
    const statusText = document.querySelector('.status-text');

    if (connected) {
        indicator.classList.add('connected');
        statusText.textContent = 'Â∑≤ËøûÊé•';
        document.getElementById('api-status').textContent = 'Ê≠£Â∏∏';
        document.getElementById('api-status').style.color = 'var(--success-color)';
    } else {
        indicator.classList.remove('connected');
        statusText.textContent = 'Êú™ËøûÊé•';
        document.getElementById('api-status').textContent = 'Êñ≠ÂºÄ';
        document.getElementById('api-status').style.color = 'var(--error-color)';
    }
}

// Ê£ÄÊü•Á≥ªÁªüÁä∂ÊÄÅ
async function checkSystemStatus() {
    try {
        const response = await fetch(`${apiUrl}/health`);
        const data = await response.json();

        document.getElementById('api-status').textContent = data.status || 'Ê≠£Â∏∏';
        document.getElementById('api-status').style.color = 'var(--success-color)';

        // Êõ¥Êñ∞ÂÖ∂‰ªñÁä∂ÊÄÅ
        if (data.database) {
            document.getElementById('db-status').textContent = data.database.status || 'Ê≠£Â∏∏';
            document.getElementById('db-status').style.color = 'var(--success-color)';
        }

        if (data.redis) {
            document.getElementById('redis-status').textContent = data.redis.status || 'Ê≠£Â∏∏';
            document.getElementById('redis-status').style.color = 'var(--success-color)';
        }

        // Êõ¥Êñ∞Âª∂Ëøü
        const latency = Date.now() - response.headers.get('X-Request-Time');
        document.getElementById('api-latency').textContent = `Âª∂Ëøü: ${latency}ms`;
    } catch (error) {
        console.error('Ê£ÄÊü•Á≥ªÁªüÁä∂ÊÄÅÂ§±Ë¥•:', error);
        if (mockMode) {
            updateMockSystemStatus();
        }
    }
}

// ÂàùÂßãÂåñÂõæË°®
function initializeChart() {
    const chartContainer = document.getElementById('price-chart');
    if (!chartContainer) return;

    chart = LightweightCharts.createChart(chartContainer, {
        width: chartContainer.clientWidth,
        height: 350,
        layout: {
            background: { color: 'transparent' },
            textColor: '#B5BBC9',
        },
        grid: {
            vertLines: { color: '#2A2F3E' },
            horzLines: { color: '#2A2F3E' },
        },
        crosshair: {
            mode: LightweightCharts.CrosshairMode.Normal,
        },
        rightPriceScale: {
            borderColor: '#2A2F3E',
        },
        timeScale: {
            borderColor: '#2A2F3E',
            timeVisible: true,
            secondsVisible: false,
        },
    });

    // Ê∑ªÂä†KÁ∫øÁ≥ªÂàó
    const candlestickSeries = chart.addCandlestickSeries({
        upColor: '#00C087',
        downColor: '#FF3B30',
        borderVisible: false,
        wickUpColor: '#00C087',
        wickDownColor: '#FF3B30',
    });

    // Ê∑ªÂä†Êàê‰∫§ÈáèÁ≥ªÂàó
    const volumeSeries = chart.addHistogramSeries({
        color: '#26a69a',
        priceFormat: {
            type: 'volume',
        },
        priceScaleId: '',
        scaleMargins: {
            top: 0.8,
            bottom: 0,
        },
    });

    // Â≠òÂÇ®Á≥ªÂàóÂºïÁî®
    chart.candlestickSeries = candlestickSeries;
    chart.volumeSeries = volumeSeries;

    // ÂìçÂ∫îÁ™óÂè£Â§ßÂ∞èÂèòÂåñ
    window.addEventListener('resize', () => {
        if (chart && chartContainer) {
            chart.applyOptions({ width: chartContainer.clientWidth });
        }
    });
}

// Ëé∑ÂèñÂ∏ÇÂú∫Êï∞ÊçÆ
async function fetchMarketData() {
    try {
        const response = await fetch(`${apiUrl}/api/v1/data/market-data/BTCUSDT`);
        const data = await response.json();

        displayMarketData(data);
    } catch (error) {
        console.error('Ëé∑ÂèñÂ∏ÇÂú∫Êï∞ÊçÆÂ§±Ë¥•:', error);
        if (mockMode) {
            loadMockMarketData();
        }
    }
}

// ÊòæÁ§∫Â∏ÇÂú∫Êï∞ÊçÆ
function displayMarketData(data) {
    const tableBody = document.querySelector('#market-table tbody');
    if (!tableBody) return;

    tableBody.innerHTML = '';

    if (Array.isArray(data)) {
        data.forEach(item => {
            const row = createMarketDataRow(item);
            tableBody.appendChild(row);
        });
    } else {
        const row = createMarketDataRow(data);
        tableBody.appendChild(row);
    }
}

// ÂàõÂª∫Â∏ÇÂú∫Êï∞ÊçÆË°å
function createMarketDataRow(item) {
    const row = document.createElement('tr');
    const changeClass = item.change24h >= 0 ? 'positive' : 'negative';

    row.innerHTML = `
        <td>${item.symbol}</td>
        <td>${formatPrice(item.price)}</td>
        <td class="${changeClass}">${formatPercent(item.change24h)}</td>
        <td>${formatVolume(item.volume24h)}</td>
        <td>${formatPrice(item.high24h)}</td>
        <td>${formatPrice(item.low24h)}</td>
    `;

    return row;
}

// Âä†ËΩΩÁ≠ñÁï•ÂàóË°®
async function loadStrategies() {
    try {
        const response = await fetch(`${apiUrl}/api/v1/strategies`);
        const strategies = await response.json();

        displayStrategies(strategies);
    } catch (error) {
        console.error('Âä†ËΩΩÁ≠ñÁï•Â§±Ë¥•:', error);
        if (mockMode) {
            loadMockStrategies();
        }
    }
}

// ÊòæÁ§∫Á≠ñÁï•ÂàóË°®
function displayStrategies(strategies) {
    const container = document.getElementById('strategies-container');
    if (!container) return;

    if (!strategies || strategies.length === 0) {
        container.innerHTML = '<div class="no-data">ÊöÇÊó†Á≠ñÁï•</div>';
        return;
    }

    container.innerHTML = '';
    strategies.forEach(strategy => {
        const item = createStrategyItem(strategy);
        container.appendChild(item);
    });

    // Êõ¥Êñ∞ÂõûÊµãÁ≠ñÁï•ÈÄâÊã©Âô®
    const backtestSelect = document.getElementById('backtest-strategy');
    if (backtestSelect) {
        backtestSelect.innerHTML = '<option value="">ÈÄâÊã©Á≠ñÁï•...</option>';
        strategies.forEach(strategy => {
            const option = document.createElement('option');
            option.value = strategy.id;
            option.textContent = strategy.name;
            backtestSelect.appendChild(option);
        });
    }
}

// ÂàõÂª∫Á≠ñÁï•È°π
function createStrategyItem(strategy) {
    const div = document.createElement('div');
    div.className = 'strategy-item';

    const statusClass = strategy.status === 'running' ? 'running' : 'stopped';

    div.innerHTML = `
        <div class="strategy-item-header">
            <span class="strategy-name">${strategy.name}</span>
            <span class="strategy-status ${statusClass}">${strategy.status}</span>
        </div>
        <div class="strategy-details">
            <small>Á±ªÂûã: ${strategy.type} | ‰∫§ÊòìÂØπ: ${strategy.symbol}</small>
        </div>
    `;

    return div;
}

// ‰øùÂ≠òÁ≠ñÁï•
async function saveStrategy() {
    const strategyData = {
        name: document.getElementById('strategy-name').value,
        type: document.getElementById('strategy-type').value,
        symbol: document.getElementById('strategy-symbol').value,
        params: JSON.parse(document.getElementById('strategy-params').value || '{}')
    };

    try {
        const response = await fetch(`${apiUrl}/api/v1/strategies`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(strategyData)
        });

        if (response.ok) {
            showToast('success', 'Á≠ñÁï•‰øùÂ≠òÊàêÂäüÔºÅ');
            loadStrategies();
            document.getElementById('strategy-form').reset();
        } else {
            throw new Error('‰øùÂ≠òÂ§±Ë¥•');
        }
    } catch (error) {
        showToast('error', `‰øùÂ≠òÁ≠ñÁï•Â§±Ë¥•: ${error.message}`);
        if (mockMode) {
            showToast('info', 'Ê®°ÊãüÊ®°ÂºèÔºöÁ≠ñÁï•Â∑≤‰øùÂ≠òÂà∞Êú¨Âú∞');
            loadMockStrategies();
        }
    }
}

// ËøêË°åÂõûÊµã
async function runBacktest() {
    const backtestData = {
        strategy_id: document.getElementById('backtest-strategy').value,
        start_date: document.getElementById('backtest-start').value,
        end_date: document.getElementById('backtest-end').value,
        initial_capital: parseFloat(document.getElementById('backtest-capital').value)
    };

    showToast('info', 'ÂõûÊµãÂºÄÂßãËøêË°å...');

    try {
        const response = await fetch(`${apiUrl}/api/v1/backtest/run`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(backtestData)
        });

        const result = await response.json();
        displayBacktestResults(result);
    } catch (error) {
        console.error('ÂõûÊµãÂ§±Ë¥•:', error);
        if (mockMode) {
            displayMockBacktestResults();
        }
    }
}

// ÊòæÁ§∫ÂõûÊµãÁªìÊûú
function displayBacktestResults(result) {
    const container = document.getElementById('backtest-results');
    if (!container) return;

    container.innerHTML = `
        <div class="backtest-results">
            <div class="result-metric">
                <div class="metric-label">ÊÄªÊî∂ÁõäÁéá</div>
                <div class="metric-value ${result.total_return >= 0 ? 'positive' : 'negative'}">
                    ${formatPercent(result.total_return)}
                </div>
            </div>
            <div class="result-metric">
                <div class="metric-label">Â§èÊôÆÊØîÁéá</div>
                <div class="metric-value">${result.sharpe_ratio?.toFixed(2) || '--'}</div>
            </div>
            <div class="result-metric">
                <div class="metric-label">ÊúÄÂ§ßÂõûÊí§</div>
                <div class="metric-value negative">${formatPercent(result.max_drawdown)}</div>
            </div>
            <div class="result-metric">
                <div class="metric-label">ËÉúÁéá</div>
                <div class="metric-value">${formatPercent(result.win_rate)}</div>
            </div>
            <div class="result-metric">
                <div class="metric-label">ÊÄª‰∫§ÊòìÊ¨°Êï∞</div>
                <div class="metric-value">${result.total_trades || 0}</div>
            </div>
            <div class="result-metric">
                <div class="metric-label">Áõà‰∫èÊØî</div>
                <div class="metric-value">${result.profit_factor?.toFixed(2) || '--'}</div>
            </div>
        </div>
    `;

    showToast('success', 'ÂõûÊµãÂÆåÊàêÔºÅ');
}

// ËøêË°åAIÂàÜÊûê
async function runAIAnalysis() {
    const analysisData = {
        type: document.getElementById('ai-analysis-type').value,
        symbol: document.getElementById('ai-symbol').value,
        model: document.getElementById('ai-model').value,
        params: document.getElementById('ai-params').value
    };

    showToast('info', 'AIÂàÜÊûêÂºÄÂßã...');

    try {
        const response = await fetch(`${apiUrl}/api/v1/ai/analyze`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(analysisData)
        });

        const result = await response.json();
        displayAIResults(result);
    } catch (error) {
        console.error('AIÂàÜÊûêÂ§±Ë¥•:', error);
        if (mockMode) {
            displayMockAIResults();
        }
    }
}

// ÊòæÁ§∫AIÂàÜÊûêÁªìÊûú
function displayAIResults(result) {
    const container = document.getElementById('ai-results');
    if (!container) return;

    const resultItem = document.createElement('div');
    resultItem.className = 'ai-result-item';

    resultItem.innerHTML = `
        <div class="ai-result-header">
            <span>${result.type || 'AIÂàÜÊûê'}</span>
            <div class="ai-confidence">
                <span>ÁΩÆ‰ø°Â∫¶:</span>
                <div class="confidence-bar">
                    <div class="confidence-fill" style="width: ${(result.confidence || 0.75) * 100}%"></div>
                </div>
                <span>${((result.confidence || 0.75) * 100).toFixed(0)}%</span>
            </div>
        </div>
        <div class="ai-result-content">
            <p>${result.analysis || 'ÂàÜÊûêÁªìÊûúÂ∞ÜÂú®ËøôÈáåÊòæÁ§∫...'}</p>
            ${result.recommendations ? `
                <div class="recommendations">
                    <strong>Âª∫ËÆÆ:</strong>
                    <ul>
                        ${result.recommendations.map(r => `<li>${r}</li>`).join('')}
                    </ul>
                </div>
            ` : ''}
        </div>
        <div class="ai-result-footer">
            <small>Ê®°Âûã: ${result.model || 'DeepSeek'} | Êó∂Èó¥: ${new Date().toLocaleString()}</small>
        </div>
    `;

    container.innerHTML = '';
    container.appendChild(resultItem);

    showToast('success', 'AIÂàÜÊûêÂÆåÊàêÔºÅ');
}

// Â∑•ÂÖ∑ÂáΩÊï∞
function formatPrice(price) {
    if (!price) return '--';
    return parseFloat(price).toLocaleString('en-US', {
        minimumFractionDigits: 2,
        maximumFractionDigits: 8
    });
}

function formatPercent(value) {
    if (!value && value !== 0) return '--';
    const percent = parseFloat(value);
    const formatted = percent.toFixed(2) + '%';
    return percent >= 0 ? '+' + formatted : formatted;
}

function formatVolume(volume) {
    if (!volume) return '--';
    const num = parseFloat(volume);
    if (num >= 1e9) return (num / 1e9).toFixed(2) + 'B';
    if (num >= 1e6) return (num / 1e6).toFixed(2) + 'M';
    if (num >= 1e3) return (num / 1e3).toFixed(2) + 'K';
    return num.toFixed(2);
}

// ToastÊ∂àÊÅØÊèêÁ§∫
function showToast(type, message) {
    const container = document.getElementById('toast-container');
    const toast = document.createElement('div');
    toast.className = `toast ${type}`;
    toast.textContent = message;

    container.appendChild(toast);

    setTimeout(() => {
        toast.style.opacity = '0';
        setTimeout(() => toast.remove(), 300);
    }, 3000);
}

