// È°µÈù¢ÂàáÊç¢
function switchPage(page) {
    console.log('ÂàáÊç¢Âà∞È°µÈù¢:', page);

    // Êõ¥Êñ∞ÂØºËà™Áä∂ÊÄÅ
    document.querySelectorAll('.nav-item').forEach(item => {
        item.classList.remove('active');
        if (item.dataset.page === page) {
            item.classList.add('active');
        }
    });

    // ÂàáÊç¢È°µÈù¢ÊòæÁ§∫
    document.querySelectorAll('.page').forEach(p => {
        p.classList.remove('active');
    });

    const targetPage = document.getElementById(`page-${page}`);
    if (targetPage) {
        targetPage.classList.add('active');
        console.log(`È°µÈù¢ ${page} Â∑≤ÊøÄÊ¥ª`);
    } else {
        console.error(`È°µÈù¢ ${page} Êú™ÊâæÂà∞`);
    }

    currentPage = page;

    // È°µÈù¢ÁâπÂÆöÁöÑÂàùÂßãÂåñ - ÊØèÊ¨°ÂàáÊç¢ÈÉΩÂä†ËΩΩÊï∞ÊçÆ
    switch(page) {
        case 'dashboard':
            loadMockPrices();
            loadMockChartData();
            break;
        case 'market':
            loadMockMarketData();
            break;
        case 'strategy':
            loadMockStrategies();
            break;
        case 'backtest':
            loadMockStrategies(); // ‰∏∫ÂõûÊµãÈ°µÈù¢Âä†ËΩΩÁ≠ñÁï•ÈÄâÈ°π
            break;
        case 'ai':
            // AIÈ°µÈù¢ÂáÜÂ§á
            break;
    }
}

// ËøûÊé•Âà∞API
async function connectToAPI() {
    const url = document.getElementById('api-url').value;
    apiUrl = url;

    // Ê£ÄÊü•APIÂÅ•Â∫∑Áä∂ÊÄÅ
    try {
        const response = await fetch(`${apiUrl}/health`);
        if (response.ok) {
            updateConnectionStatus(true);
            showToast('success', 'APIËøûÊé•ÊàêÂäüÔºÅ');

            // Â∞ùËØïÂª∫Á´ãWebSocketËøûÊé•
            connectWebSocket();

            // Âä†ËΩΩÂàùÂßãÊï∞ÊçÆ
            checkSystemStatus();
        } else {
            throw new Error('APIËøûÊé•Â§±Ë¥•');
        }
    } catch (error) {
        updateConnectionStatus(false);

        if (mockMode) {
            showToast('warning', 'Êó†Ê≥ïËøûÊé•Âà∞APIÔºå‰ΩøÁî®Ê®°ÊãüÊï∞ÊçÆÊ®°Âºè');
            updateConnectionStatus(true);
            initializeMockWebSocket();
        } else {
            showToast('error', `ËøûÊé•Â§±Ë¥•: ${error.message}`);
        }
    }
}

// WebSocketËøûÊé•
function connectWebSocket() {
    const wsUrl = apiUrl.replace('http', 'ws') + '/ws';

    try {
        wsConnection = new WebSocket(wsUrl);

        wsConnection.onopen = () => {
            console.log('WebSocketËøûÊé•ÊàêÂäü');
            document.getElementById('websocket-status').textContent = 'Â∑≤ËøûÊé•';
            document.getElementById('websocket-status').style.color = 'var(--success-color)';
        };

        wsConnection.onmessage = (event) => {
            handleWebSocketMessage(JSON.parse(event.data));
        };

        wsConnection.onerror = (error) => {
            console.error('WebSocketÈîôËØØ:', error);
            document.getElementById('websocket-status').textContent = 'ËøûÊé•ÈîôËØØ';
            document.getElementById('websocket-status').style.color = 'var(--error-color)';
        };

        wsConnection.onclose = () => {
            console.log('WebSocketËøûÊé•ÂÖ≥Èó≠');
            document.getElementById('websocket-status').textContent = 'Â∑≤Êñ≠ÂºÄ';
            document.getElementById('websocket-status').style.color = 'var(--warning-color)';

            // 5ÁßíÂêéÂ∞ùËØïÈáçËøû
            setTimeout(() => {
                if (!wsConnection || wsConnection.readyState === WebSocket.CLOSED) {
                    connectWebSocket();
                }
            }, 5000);
        };
    } catch (error) {
        console.error('WebSocketËøûÊé•Â§±Ë¥•:', error);
        if (mockMode) {
            initializeMockWebSocket();
        }
    }
}

// Â§ÑÁêÜWebSocketÊ∂àÊÅØ
function handleWebSocketMessage(data) {
    switch (data.type) {
        case 'price_update':
            updatePriceDisplay(data.symbol, data.price, data.change);
            if (chart && data.symbol === document.getElementById('symbol-select')?.value) {
                updateChartRealtime(data);
            }
            break;
        case 'market_stats':
            updateMarketStats(data);
            break;
        case 'strategy_update':
            updateStrategyPerformance(data);
            break;
        default:
            console.log('Êî∂Âà∞WebSocketÊ∂àÊÅØ:', data);
    }

    // Êõ¥Êñ∞ÊúÄÂêéÊõ¥Êñ∞Êó∂Èó¥
    document.getElementById('data-update-time').textContent = 
        `ÊúÄÂêéÊõ¥Êñ∞: ${new Date().toLocaleTimeString()}`;
}

// Êõ¥Êñ∞ËøûÊé•Áä∂ÊÄÅ
function updateConnectionStatus(connected) {
    const indicator = document.getElementById('ws-status');
    const statusText = document.querySelector('.status-text');

    if (connected) {
        indicator.classList.add('connected');
        statusText.textContent = 'Â∑≤ËøûÊé•';
        document.getElementById('api-status').textContent = 'Ê≠£Â∏∏';
        document.getElementById('api-status').style.color = 'var(--success-color)';
    } else {
        indicator.classList.remove('connected');
        statusText.textContent = 'Êú™ËøûÊé•';
        document.getElementById('api-status').textContent = 'Êñ≠ÂºÄ';
        document.getElementById('api-status').style.color = 'var(--error-color)';
    }
}

// Ê£ÄÊü•Á≥ªÁªüÁä∂ÊÄÅ
async function checkSystemStatus() {
    try {
        const response = await fetch(`${apiUrl}/health`);
        const data = await response.json();

        document.getElementById('api-status').textContent = data.status || 'Ê≠£Â∏∏';
        document.getElementById('api-status').style.color = 'var(--success-color)';

        // Êõ¥Êñ∞ÂÖ∂‰ªñÁä∂ÊÄÅ
        if (data.database) {
            document.getElementById('db-status').textContent = data.database.status || 'Ê≠£Â∏∏';
            document.getElementById('db-status').style.color = 'var(--success-color)';
        }

        if (data.redis) {
            document.getElementById('redis-status').textContent = data.redis.status || 'Ê≠£Â∏∏';
            document.getElementById('redis-status').style.color = 'var(--success-color)';
        }

        // Êõ¥Êñ∞Âª∂Ëøü
        const latency = Date.now() - response.headers.get('X-Request-Time');
        document.getElementById('api-latency').textContent = `Âª∂Ëøü: ${latency}ms`;
    } catch (error) {
        console.error('Ê£ÄÊü•Á≥ªÁªüÁä∂ÊÄÅÂ§±Ë¥•:', error);
        if (mockMode) {
            updateMockSystemStatus();
        }
    }
}

// ÂàùÂßãÂåñÂõæË°®
function initializeChart() {
    const chartContainer = document.getElementById('price-chart');
    if (!chartContainer) {
        console.warn('ÂõæË°®ÂÆπÂô®Êú™ÊâæÂà∞ÔºåË∑≥ËøáÂõæË°®ÂàùÂßãÂåñ');
        return;
    }

    console.log('ÂàùÂßãÂåñÂõæË°®...');

    try {
        chart = LightweightCharts.createChart(chartContainer, {
        width: chartContainer.clientWidth,
        height: 350,
        layout: {
            background: { color: 'transparent' },
            textColor: '#B5BBC9',
        },
        grid: {
            vertLines: { color: '#2A2F3E' },
            horzLines: { color: '#2A2F3E' },
        },
        crosshair: {
            mode: LightweightCharts.CrosshairMode.Normal,
        },
        rightPriceScale: {
            borderColor: '#2A2F3E',
        },
        timeScale: {
            borderColor: '#2A2F3E',
            timeVisible: true,
            secondsVisible: false,
        },
    });

    // Ê∑ªÂä†KÁ∫øÁ≥ªÂàó
    const candlestickSeries = chart.addCandlestickSeries({
        upColor: '#00C087',
        downColor: '#FF3B30',
        borderVisible: false,
        wickUpColor: '#00C087',
        wickDownColor: '#FF3B30',
    });

    // Ê∑ªÂä†Êàê‰∫§ÈáèÁ≥ªÂàó
    const volumeSeries = chart.addHistogramSeries({
        color: '#26a69a',
        priceFormat: {
            type: 'volume',
        },
        priceScaleId: '',
        scaleMargins: {
            top: 0.8,
            bottom: 0,
        },
    });

    // Â≠òÂÇ®Á≥ªÂàóÂºïÁî®
    chart.candlestickSeries = candlestickSeries;
    chart.volumeSeries = volumeSeries;

    // ÂìçÂ∫îÁ™óÂè£Â§ßÂ∞èÂèòÂåñ
    window.addEventListener('resize', () => {
        if (chart && chartContainer) {
            chart.applyOptions({ width: chartContainer.clientWidth });
        }
    });
}

