// ÊòæÁ§∫Â∏ÇÂú∫Êï∞ÊçÆ
function displayMarketData(data) {
    const tableBody = document.querySelector('#market-table tbody');
    if (!tableBody) return;

    tableBody.innerHTML = '';

    if (Array.isArray(data)) {
        data.forEach(item => {
            const row = createMarketDataRow(item);
            tableBody.appendChild(row);
        });
    } else {
        const row = createMarketDataRow(data);
        tableBody.appendChild(row);
    }
}

// ÂàõÂª∫Â∏ÇÂú∫Êï∞ÊçÆË°å
function createMarketDataRow(item) {
    const row = document.createElement('tr');
    const changeClass = item.change24h >= 0 ? 'positive' : 'negative';

    row.innerHTML = `
        <td>${item.symbol}</td>
        <td>${formatPrice(item.price)}</td>
        <td class="${changeClass}">${formatPercent(item.change24h)}</td>
        <td>${formatVolume(item.volume24h)}</td>
        <td>${formatPrice(item.high24h)}</td>
        <td>${formatPrice(item.low24h)}</td>
    `;

    return row;
}

