function loadMockChartData() {
    if (!chart || !chart.candlestickSeries) {
        console.warn('ÂõæË°®Êú™ÂàùÂßãÂåñÔºåÂª∂ËøüÂä†ËΩΩÊï∞ÊçÆ...');
