// ÊµãËØïÂ•ó‰ª∂ - Âä†ÂØÜË¥ßÂ∏ÅÈáèÂåñÂàÜÊûêÁ≥ªÁªü
class TestSuite {
    constructor() {
        this.tests = [];
        this.currentTestIndex = 0;
        this.isRunning = false;
        this.startTime = null;
        this.results = {
            total: 0,
            passed: 0,
            failed: 0,
            skipped: 0
        };
        this.targetUrl = 'http://localhost:8000/index.html';
        this.apiEndpoint = 'http://localhost:8080';
        this.testDelay = 1000;

        this.initializeTests();
        this.setupEventListeners();
        this.renderTestCases();
    }

    // ÂàùÂßãÂåñÊâÄÊúâÊµãËØïÁî®‰æã
    initializeTests() {
        this.tests = [
            // ========== ÁïåÈù¢Âä†ËΩΩÊµãËØï ==========
            {
                id: 'ui-load-1',
                category: 'ÁïåÈù¢Âä†ËΩΩ',
                name: 'È°µÈù¢Âü∫Á°ÄÂä†ËΩΩÊµãËØï',
                description: 'È™åËØÅÈ°µÈù¢ËÉΩÂê¶Ê≠£Â∏∏Âä†ËΩΩ',
                async run() {
                    this.log('info', 'ÂºÄÂßãÈ°µÈù¢Âä†ËΩΩÊµãËØï...');

                    try {
                        const response = await fetch(this.targetUrl);
                        if (!response.ok) {
                            throw new Error(`HTTPÈîôËØØ: ${response.status}`);
                        }

                        const html = await response.text();
                        if (!html.includes('<!DOCTYPE html>')) {
                            throw new Error('‰∏çÊòØÊúâÊïàÁöÑHTMLÈ°µÈù¢');
                        }

                        this.log('success', '‚úÖ È°µÈù¢Âä†ËΩΩÊàêÂäü');
                        return { success: true, message: 'È°µÈù¢Ê≠£Â∏∏Âä†ËΩΩ' };
                    } catch (error) {
                        this.log('error', `‚ùå È°µÈù¢Âä†ËΩΩÂ§±Ë¥•: ${error.message}`);
                        return { success: false, message: error.message };
                    }
                }
            },
            {
                id: 'ui-load-2',
                category: 'ÁïåÈù¢Âä†ËΩΩ',
                name: 'Ê†∑ÂºèÊñá‰ª∂Âä†ËΩΩÊµãËØï',
                description: 'È™åËØÅCSSÊ†∑ÂºèÊòØÂê¶Ê≠£Á°ÆÂä†ËΩΩ',
                async run() {
                    this.log('info', 'Ê£ÄÊü•Ê†∑ÂºèÊñá‰ª∂...');

                    try {
                        const cssUrl = this.targetUrl.replace('index.html', 'styles.css');
                        const response = await fetch(cssUrl);

                        if (!response.ok) {
                            throw new Error('Ê†∑ÂºèÊñá‰ª∂Âä†ËΩΩÂ§±Ë¥•');
                        }

                        const css = await response.text();
                        if (!css.includes(':root')) {
                            throw new Error('Ê†∑ÂºèÊñá‰ª∂Ê†ºÂºè‰∏çÊ≠£Á°Æ');
                        }

                        this.log('success', '‚úÖ Ê†∑ÂºèÊñá‰ª∂Âä†ËΩΩÊàêÂäü');
                        return { success: true };
                    } catch (error) {
                        this.log('error', `‚ùå Ê†∑ÂºèÂä†ËΩΩÂ§±Ë¥•: ${error.message}`);
                        return { success: false, message: error.message };
                    }
                }
            },
            {
                id: 'ui-load-3',
                category: 'ÁïåÈù¢Âä†ËΩΩ',
                name: 'JavaScriptÂä†ËΩΩÊµãËØï',
                description: 'È™åËØÅJavaScriptÊñá‰ª∂ÊòØÂê¶Ê≠£Á°ÆÂä†ËΩΩ',
                async run() {
                    this.log('info', 'Ê£ÄÊü•JavaScriptÊñá‰ª∂...');

                    try {
                        const jsUrl = this.targetUrl.replace('index.html', 'app.js');
                        const response = await fetch(jsUrl);

                        if (!response.ok) {
                            throw new Error('JavaScriptÊñá‰ª∂Âä†ËΩΩÂ§±Ë¥•');
                        }

                        const js = await response.text();
                        if (!js.includes('initializeApp')) {
                            throw new Error('JavaScriptÊñá‰ª∂ÂÜÖÂÆπ‰∏çÂÆåÊï¥');
                        }

                        this.log('success', '‚úÖ JavaScriptÊñá‰ª∂Âä†ËΩΩÊàêÂäü');
                        return { success: true };
                    } catch (error) {
                        this.log('error', `‚ùå JavaScriptÂä†ËΩΩÂ§±Ë¥•: ${error.message}`);
                        return { success: false, message: error.message };
                    }
                }
            },

            // ========== ÂØºËà™ÂäüËÉΩÊµãËØï ==========
            {
                id: 'nav-1',
                category: 'ÂØºËà™ÂäüËÉΩ',
                name: 'È°µÈù¢ÂàáÊç¢ÊµãËØï',
                description: 'ÊµãËØïÂêÑÈ°µÈù¢‰πãÈó¥ÁöÑÂàáÊç¢ÂäüËÉΩ',
                async run() {
                    this.log('info', 'ÊµãËØïÈ°µÈù¢ÂØºËà™ÂäüËÉΩ...');

                    const pages = ['dashboard', 'market', 'strategy', 'backtest', 'ai'];
                    const results = [];

                    for (const page of pages) {
                        this.log('info', `ÂàáÊç¢Âà∞ ${page} È°µÈù¢...`);

                        // Ê®°ÊãüÈ°µÈù¢ÂàáÊç¢
                        const testResult = await this.simulatePageSwitch(page);
                        results.push(testResult);

                        if (testResult) {
                            this.log('success', `‚úÖ ${page} È°µÈù¢ÂàáÊç¢ÊàêÂäü`);
                        } else {
                            this.log('error', `‚ùå ${page} È°µÈù¢ÂàáÊç¢Â§±Ë¥•`);
                        }

                        await this.delay(500);
                    }

                    const allSuccess = results.every(r => r === true);
                    return { 
                        success: allSuccess, 
                        message: allSuccess ? 'ÊâÄÊúâÈ°µÈù¢ÂàáÊç¢Ê≠£Â∏∏' : 'ÈÉ®ÂàÜÈ°µÈù¢ÂàáÊç¢Â§±Ë¥•' 
                    };
                }
            },

            // ========== Êï∞ÊçÆÂ±ïÁ§∫ÊµãËØï ==========
            {
                id: 'data-1',
                category: 'Êï∞ÊçÆÂ±ïÁ§∫',
                name: 'Ê®°ÊãüÊï∞ÊçÆÂä†ËΩΩÊµãËØï',
                description: 'È™åËØÅÊ®°ÊãüÊï∞ÊçÆÊòØÂê¶Ê≠£Á°ÆÂä†ËΩΩ',
                async run() {
                    this.log('info', 'Ê£ÄÊü•Ê®°ÊãüÊï∞ÊçÆÂä†ËΩΩ...');

                    const checks = [
                        { element: 'btc-price', name: 'BTC‰ª∑Ê†º' },
                        { element: 'eth-price', name: 'ETH‰ª∑Ê†º' },
                        { element: 'api-status', name: 'APIÁä∂ÊÄÅ' },
                        { element: 'websocket-status', name: 'WebSocketÁä∂ÊÄÅ' }
                    ];

                    const results = [];
                    for (const check of checks) {
                        const hasData = await this.checkElementContent(check.element);
                        if (hasData) {
                            this.log('success', `‚úÖ ${check.name}Êï∞ÊçÆÂ∑≤Âä†ËΩΩ`);
                            results.push(true);
                        } else {
                            this.log('warning', `‚ö†Ô∏è ${check.name}Êï∞ÊçÆÊú™ÊâæÂà∞`);
                            results.push(false);
                        }
                    }

                    const successRate = results.filter(r => r).length / results.length;
                    return { 
                        success: successRate > 0.7, 
                        message: `Êï∞ÊçÆÂä†ËΩΩÁéá: ${(successRate * 100).toFixed(0)}%` 
                    };
                }
            },
            {
                id: 'data-2',
                category: 'Êï∞ÊçÆÂ±ïÁ§∫',
                name: 'ÂÆûÊó∂‰ª∑Ê†ºÊõ¥Êñ∞ÊµãËØï',
                description: 'È™åËØÅ‰ª∑Ê†ºÊòØÂê¶ÂÆûÊó∂Êõ¥Êñ∞',
                async run() {
                    this.log('info', 'ÁõëÊµãÂÆûÊó∂‰ª∑Ê†ºÊõ¥Êñ∞...');

                    // ËÆ∞ÂΩïÂàùÂßã‰ª∑Ê†º
                    const initialPrice = await this.getElementValue('btc-price');
                    this.log('info', `ÂàùÂßãBTC‰ª∑Ê†º: ${initialPrice}`);

                    // Á≠âÂæÖ3Áßí
                    this.log('info', 'Á≠âÂæÖ‰ª∑Ê†ºÊõ¥Êñ∞...');
                    await this.delay(3000);

                    // Ê£ÄÊü•‰ª∑Ê†ºÊòØÂê¶ÂèòÂåñ
                    const newPrice = await this.getElementValue('btc-price');
                    this.log('info', `Êõ¥Êñ∞ÂêéBTC‰ª∑Ê†º: ${newPrice}`);

                    if (initialPrice !== newPrice && newPrice !== '--') {
                        this.log('success', '‚úÖ ‰ª∑Ê†ºÂÆûÊó∂Êõ¥Êñ∞Ê≠£Â∏∏');
                        return { success: true, message: '‰ª∑Ê†ºÊõ¥Êñ∞ÂäüËÉΩÊ≠£Â∏∏' };
                    } else {
                        this.log('warning', '‚ö†Ô∏è ‰ª∑Ê†ºÊú™ÂèëÁîüÊõ¥Êñ∞');
                        return { success: false, message: '‰ª∑Ê†ºÊú™Êõ¥Êñ∞' };
                    }
                }
            },

            // ========== ÂõæË°®ÂäüËÉΩÊµãËØï ==========
            {
                id: 'chart-1',
                category: 'ÂõæË°®ÂäüËÉΩ',
                name: 'KÁ∫øÂõæË°®Âä†ËΩΩÊµãËØï',
                description: 'È™åËØÅKÁ∫øÂõæË°®ÊòØÂê¶Ê≠£Á°ÆÂä†ËΩΩ',
                async run() {
                    this.log('info', 'Ê£ÄÊü•KÁ∫øÂõæË°®...');

                    // Ê£ÄÊü•ÂõæË°®ÂÆπÂô®
                    const hasChart = await this.checkElementExists('price-chart');

                    if (hasChart) {
                        this.log('success', '‚úÖ KÁ∫øÂõæË°®ÂÆπÂô®Â≠òÂú®');

                        // Ê£ÄÊü•ÊòØÂê¶ÊúâÊï∞ÊçÆ
                        await this.delay(1000);
                        this.log('info', 'È™åËØÅÂõæË°®Êï∞ÊçÆ...');

                        // ËøôÈáåÁÆÄÂåñÊµãËØïÔºåÂÆûÈôÖÂ∫îËØ•Ê£ÄÊü•canvasÂÜÖÂÆπ
                        this.log('success', '‚úÖ ÂõæË°®Â∑≤Ê∏≤Êüì');
                        return { success: true, message: 'ÂõæË°®Âä†ËΩΩÊ≠£Â∏∏' };
                    } else {
                        this.log('error', '‚ùå ÂõæË°®ÂÆπÂô®Êú™ÊâæÂà∞');
                        return { success: false, message: 'ÂõæË°®Âä†ËΩΩÂ§±Ë¥•' };
                    }
                }
            },
            {
                id: 'chart-2',
                category: 'ÂõæË°®ÂäüËÉΩ',
                name: 'ÂõæË°®Êó∂Èó¥Âë®ÊúüÂàáÊç¢ÊµãËØï',
                description: 'ÊµãËØïÂõæË°®Êó∂Èó¥Âë®ÊúüÂàáÊç¢ÂäüËÉΩ',
                async run() {
                    this.log('info', 'ÊµãËØïÊó∂Èó¥Âë®ÊúüÂàáÊç¢...');

                    const timeframes = ['1m', '5m', '15m', '1h', '4h', '1d'];
                    const results = [];

                    for (const tf of timeframes) {
                        this.log('info', `ÂàáÊç¢Âà∞ ${tf} Êó∂Èó¥Âë®Êúü...`);

                        // Ê®°ÊãüÈÄâÊã©Êó∂Èó¥Âë®Êúü
                        const success = await this.simulateSelectChange('timeframe-select', tf);
                        results.push(success);

                        if (success) {
                            this.log('success', `‚úÖ ${tf} Âë®ÊúüÂàáÊç¢ÊàêÂäü`);
                        } else {
                            this.log('warning', `‚ö†Ô∏è ${tf} Âë®ÊúüÂàáÊç¢Â§±Ë¥•`);
                        }

                        await this.delay(300);
                    }

                    const successRate = results.filter(r => r).length / results.length;
                    return { 
                        success: successRate > 0.8, 
                        message: `ÂàáÊç¢ÊàêÂäüÁéá: ${(successRate * 100).toFixed(0)}%` 
                    };
                }
            },

            // ========== Ë°®ÂçïÂäüËÉΩÊµãËØï ==========
            {
                id: 'form-1',
                category: 'Ë°®ÂçïÂäüËÉΩ',
                name: 'Á≠ñÁï•ÂàõÂª∫Ë°®ÂçïÊµãËØï',
                description: 'ÊµãËØïÁ≠ñÁï•ÂàõÂª∫Ë°®ÂçïÈ™åËØÅ',
                async run() {
                    this.log('info', 'ÊµãËØïÁ≠ñÁï•ÂàõÂª∫Ë°®Âçï...');

                    // ÂàáÊç¢Âà∞Á≠ñÁï•È°µÈù¢
                    await this.simulatePageSwitch('strategy');
                    await this.delay(500);

