        this.tests.forEach(test => {
            if (!categories[test.category]) {
                categories[test.category] = [];
            }
            categories[test.category].push(test);
        });

        // Ê∏≤ÊüìÊØè‰∏™Á±ªÂà´
        Object.entries(categories).forEach(([category, tests]) => {
            const categoryDiv = document.createElement('div');
            categoryDiv.innerHTML = `<h4 style="margin: 10px 0; color: var(--primary-color)">${category}</h4>`;

            tests.forEach(test => {
                const testDiv = document.createElement('div');
                testDiv.className = 'test-item';
                testDiv.id = `test-${test.id}`;
                testDiv.innerHTML = `
                    <div class="test-item-header">
                        <span class="test-name">${test.name}</span>
                        <span class="test-status status-pending">ÂæÖÊµã</span>
                    </div>
                    <div class="test-time">${test.description}</div>
                `;

                testDiv.addEventListener('click', () => this.runSingleTest(test));
                categoryDiv.appendChild(testDiv);
            });

            container.appendChild(categoryDiv);
        });

        document.getElementById('totalTests').textContent = this.tests.length;
    }

    // ËøêË°åÊâÄÊúâÊµãËØï
    async runAllTests() {
        if (this.isRunning) return;

        this.isRunning = true;
        this.startTime = Date.now();
        this.resetResults();

        document.getElementById('runAllTests').disabled = true;
        document.getElementById('runSelectedTests').disabled = true;

        this.log('info', '========== ÂºÄÂßãËøêË°åÊâÄÊúâÊµãËØï ==========');

        for (let i = 0; i < this.tests.length; i++) {
            if (!this.isRunning) break;

            const test = this.tests[i];
            await this.runTest(test);

            // Êõ¥Êñ∞ËøõÂ∫¶Êù°
            const progress = ((i + 1) / this.tests.length) * 100;
            this.updateProgress(progress);

            // Âª∂Ëøü
            if (this.testDelay > 0) {
                await this.delay(this.testDelay);
            }
        }

        this.finishTesting();
    }

    // ËøêË°åÂçï‰∏™ÊµãËØï
    async runTest(test) {
        const testElement = document.getElementById(`test-${test.id}`);
        const statusElement = testElement.querySelector('.test-status');

        // Êõ¥Êñ∞UIÁä∂ÊÄÅ
        testElement.className = 'test-item running';
        statusElement.className = 'test-status status-running';
        statusElement.textContent = 'ËøêË°å‰∏≠';

        document.getElementById('currentTest').innerHTML = 
            `${test.name} <span class="running-indicator"></span>`;

        this.log('info', `\n‚ñ∂ ËøêË°åÊµãËØï: ${test.name}`);
        this.log('info', `ÊèèËø∞: ${test.description}`);

        try {
            // ÁªëÂÆö‰∏ä‰∏ãÊñáÂπ∂ËøêË°åÊµãËØï
            const result = await test.run.call(this);

            if (result.success) {
                testElement.className = 'test-item success';
                statusElement.className = 'test-status status-success';
                statusElement.textContent = 'ÈÄöËøá';
                this.results.passed++;
                this.log('success', `‚úÖ ÊµãËØïÈÄöËøá: ${result.message || 'ÊàêÂäü'}`);
            } else {
                testElement.className = 'test-item failed';
                statusElement.className = 'test-status status-failed';
                statusElement.textContent = 'Â§±Ë¥•';
                this.results.failed++;
                this.log('error', `‚ùå ÊµãËØïÂ§±Ë¥•: ${result.message || 'Â§±Ë¥•'}`);
            }
        } catch (error) {
            testElement.className = 'test-item failed';
            statusElement.className = 'test-status status-failed';
            statusElement.textContent = 'ÈîôËØØ';
            this.results.failed++;
            this.log('error', `‚ùå ÊµãËØïÂºÇÂ∏∏: ${error.message}`);
        }

        this.results.total++;
        this.updateStats();
    }

    // ËøêË°åÂçï‰∏™ÊµãËØïÔºàÁÇπÂáªËß¶ÂèëÔºâ
    async runSingleTest(test) {
        if (this.isRunning) return;

        this.isRunning = true;
        this.startTime = Date.now();

        await this.runTest(test);

        this.isRunning = false;
        this.updateStats();
    }

    // ÂÅúÊ≠¢ÊµãËØï
    stopTests() {
        this.isRunning = false;
        this.log('warning', '‚èπ ÊµãËØïÂ∑≤ÂÅúÊ≠¢');
        document.getElementById('runAllTests').disabled = false;
        document.getElementById('runSelectedTests').disabled = false;
    }

    // Ê∏ÖÁ©∫ÁªìÊûú
    clearResults() {
        document.getElementById('outputLog').innerHTML = '';
        this.resetResults();
        this.renderTestCases();
        this.log('info', 'ÁªìÊûúÂ∑≤Ê∏ÖÁ©∫');
    }

    // ÈáçÁΩÆÁªìÊûúÁªüËÆ°
    resetResults() {
        this.results = {
            total: 0,
            passed: 0,
            failed: 0,
            skipped: 0
        };
        this.updateStats();
    }

    // Êõ¥Êñ∞ÁªüËÆ°‰ø°ÊÅØ
    updateStats() {
        document.getElementById('totalTests').textContent = this.tests.length;
        document.getElementById('passedTests').textContent = this.results.passed;
        document.getElementById('failedTests').textContent = this.results.failed;
        document.getElementById('skippedTests').textContent = this.results.skipped;

        if (this.startTime) {
            const elapsed = ((Date.now() - this.startTime) / 1000).toFixed(1);
            document.getElementById('testTime').textContent = `${elapsed}s`;
        }

        if (this.results.total > 0) {
            const rate = ((this.results.passed / this.results.total) * 100).toFixed(0);
            document.getElementById('successRate').textContent = `${rate}%`;
        }
    }

    // Êõ¥Êñ∞ËøõÂ∫¶Êù°
    updateProgress(percent) {
        const progressBar = document.getElementById('progressBar');
        progressBar.style.width = `${percent}%`;
        progressBar.textContent = `${percent.toFixed(0)}%`;
    }

    // ÂÆåÊàêÊµãËØï
    finishTesting() {
        this.isRunning = false;
        document.getElementById('runAllTests').disabled = false;
        document.getElementById('runSelectedTests').disabled = false;
        document.getElementById('currentTest').textContent = 'ÊµãËØïÂÆåÊàê';

        const elapsed = ((Date.now() - this.startTime) / 1000).toFixed(1);
        this.log('info', '========== ÊµãËØïÂÆåÊàê ==========');
        this.log('info', `ÊÄªËÄóÊó∂: ${elapsed}Áßí`);
        this.log('info', `ÈÄöËøá: ${this.results.passed} | Â§±Ë¥•: ${this.results.failed} | Ë∑≥Ëøá: ${this.results.skipped}`);

        const successRate = ((this.results.passed / this.results.total) * 100).toFixed(1);
        if (successRate >= 80) {
            this.log('success', `‚úÖ ÊµãËØïÊàêÂäüÁéá: ${successRate}% - ‰ºòÁßÄ`);
        } else if (successRate >= 60) {
            this.log('warning', `‚ö†Ô∏è ÊµãËØïÊàêÂäüÁéá: ${successRate}% - ÈúÄË¶ÅÊîπËøõ`);
        } else {
            this.log('error', `‚ùå ÊµãËØïÊàêÂäüÁéá: ${successRate}% - Â≠òÂú®ÈóÆÈ¢ò`);
        }
    }

    // ÂØºÂá∫Êä•Âëä
    exportReport() {
        const report = {
            timestamp: new Date().toISOString(),
            duration: this.startTime ? (Date.now() - this.startTime) / 1000 : 0,
            results: this.results,
            tests: this.tests.map(test => {
                const element = document.getElementById(`test-${test.id}`);
                const status = element ? element.querySelector('.test-status').textContent : 'Êú™ËøêË°å';
                return {
                    id: test.id,
                    category: test.category,
                    name: test.name,
                    description: test.description,
                    status: status
                };
            }),
            logs: Array.from(document.querySelectorAll('.log-entry')).map(entry => entry.textContent)
        };

        const blob = new Blob([JSON.stringify(report, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `test-report-${Date.now()}.json`;
        a.click();

        this.log('success', '‚úÖ Êä•ÂëäÂ∑≤ÂØºÂá∫');
    }

    // ÊòæÁ§∫/ÈöêËóèUI
    toggleUI() {
        const container = document.getElementById('uiContainer');
        const frame = document.getElementById('targetFrame');

        if (container.style.display === 'block') {
            container.style.display = 'none';
        } else {
            container.style.display = 'block';
            if (!frame.src) {
                frame.src = this.targetUrl;
            }
        }
    }

    // ÊâìÂºÄÁõÆÊ†áÈ°µÈù¢
    openTarget() {
        window.open(this.targetUrl, '_blank');
    }

    // ÊµãËØïAPIËøûÊé•
    async testAPIConnection() {
        this.log('info', `ÊµãËØïAPIËøûÊé•: ${this.apiEndpoint}`);

        try {
            const response = await fetch(`${this.apiEndpoint}/health`);
            if (response.ok) {
                this.log('success', '‚úÖ APIËøûÊé•ÊàêÂäü');
            } else {
                this.log('error', `‚ùå APIËøîÂõûÈîôËØØ: ${response.status}`);
            }
        } catch (error) {
            this.log('error', `‚ùå ËøûÊé•Â§±Ë¥•: ${error.message}`);
        }
    }
}

// ÂàùÂßãÂåñÊµãËØïÂ•ó‰ª∂
document.addEventListener('DOMContentLoaded', () => {
    window.testSuite = new TestSuite();
    console.log('ÊµãËØïÂ•ó‰ª∂Â∑≤Âä†ËΩΩ');
});

