// ÂéÜÂè≤Êï∞ÊçÆÂä†ËΩΩÂô® - ÂØπÊé•CoinGlassÂéÜÂè≤‰∫§ÊòìÊï∞ÊçÆ
class HistoricalDataLoader {
    constructor() {
        this.dataPath = 'E:/historical data';
        this.supportedDataTypes = [
            'kline',           // KÁ∫øÊï∞ÊçÆ
            'funding_rate',    // ËµÑÈáëË¥πÁéá
            'liquidation',     // ÁàÜ‰ªìÊï∞ÊçÆ
            'open_interest',   // ÊåÅ‰ªìÊï∞ÊçÆ
            'long_short_ratio',// Â§öÁ©∫ÊØî
            'market_data',     // Â∏ÇÂú∫Êï∞ÊçÆ
            'index_data',      // ÊåáÊï∞Êï∞ÊçÆ
            'etf_data',        // ETFÊï∞ÊçÆ
            'spot_price'       // Áé∞Ë¥ß‰ª∑Ê†º
        ];

        this.timeframes = {
            '1m': '1ÂàÜÈíü',
            '5m': '5ÂàÜÈíü',
            '15m': '15ÂàÜÈíü',
            '1h': '1Â∞èÊó∂',
            '4h': '4Â∞èÊó∂',
            '1d': '1Â§©',
            '1w': '1Âë®',
            '1M': '1Êúà'
        };

        this.exchanges = ['Binance', 'OKX', 'Bybit', 'Deribit', 'Bitget'];
        this.symbols = ['BTCUSDT', 'ETHUSDT', 'BNBUSDT', 'ADAUSDT', 'XRPUSDT', 'SOLUSDT', 'DOGEUSDT'];

        this.cache = new Map();
        this.metadata = null;
    }

    // ÂàùÂßãÂåñÊï∞ÊçÆÂä†ËΩΩÂô®
    async initialize() {
        console.log('ÂàùÂßãÂåñÂéÜÂè≤Êï∞ÊçÆÂä†ËΩΩÂô®...');

        try {
            // Âä†ËΩΩÂÖÉÊï∞ÊçÆ
            await this.loadMetadata();

            // Ê£ÄÊü•Êï∞ÊçÆÁõÆÂΩï
            await this.checkDataDirectories();

            console.log('ÂéÜÂè≤Êï∞ÊçÆÂä†ËΩΩÂô®ÂàùÂßãÂåñÊàêÂäü');
            return true;
        } catch (error) {
            console.error('ÂàùÂßãÂåñÂ§±Ë¥•:', error);
            return false;
        }
    }

    // Âä†ËΩΩÂÖÉÊï∞ÊçÆ
    async loadMetadata() {
        try {
            // Âú®ÂÆûÈôÖÁéØÂ¢É‰∏≠ÔºåËøôÈáåÂ∫îËØ•‰ªéÊñá‰ª∂Á≥ªÁªüËØªÂèñ
            // Ê®°ÊãüÂä†ËΩΩÂÖÉÊï∞ÊçÆ
            this.metadata = {
                download_time: "2025-08-24T10:35:32",
                data_types: {
                    kline: { raw_files: 30576, processed_files: 0 },
                    funding_rate: { raw_files: 17472, processed_files: 0 },
                    liquidation: { raw_files: 17471, processed_files: 0 },
                    open_interest: { raw_files: 17472, processed_files: 0 },
                    index_data: { raw_files: 4, processed_files: 4 }
                },
                date_range: {
                    start: '2013-01-01',
                    end: '2025-12-31'
                }
            };

            console.log('ÂÖÉÊï∞ÊçÆÂä†ËΩΩÊàêÂäü:', this.metadata);
            return this.metadata;
        } catch (error) {
            console.error('Âä†ËΩΩÂÖÉÊï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // Ê£ÄÊü•Êï∞ÊçÆÁõÆÂΩï
    async checkDataDirectories() {
        const directories = {
            raw: `${this.dataPath}/raw_data`,
            processed: `${this.dataPath}/processed_data`,
            metadata: `${this.dataPath}/metadata`
        };

        console.log('Êï∞ÊçÆÁõÆÂΩïÁªìÊûÑ:', directories);
        return directories;
    }

    // Âä†ËΩΩKÁ∫øÊï∞ÊçÆ
    async loadKlineData(symbol, exchange, timeframe, startDate, endDate) {
        const cacheKey = `kline_${symbol}_${exchange}_${timeframe}_${startDate}_${endDate}`;

        // Ê£ÄÊü•ÁºìÂ≠ò
        if (this.cache.has(cacheKey)) {
            console.log('‰ªéÁºìÂ≠òÂä†ËΩΩÊï∞ÊçÆ');
            return this.cache.get(cacheKey);
        }

        console.log(`Âä†ËΩΩKÁ∫øÊï∞ÊçÆ: ${symbol} ${exchange} ${timeframe}`);

        try {
            // ÁîüÊàêÊ®°ÊãüÊï∞ÊçÆÔºàÂÆûÈôÖÂ∫îËØ•‰ªéÊñá‰ª∂ËØªÂèñÔºâ
            const data = this.generateMockKlineData(symbol, timeframe, startDate, endDate);

            // ÁºìÂ≠òÊï∞ÊçÆ
            this.cache.set(cacheKey, data);

            return data;
        } catch (error) {
            console.error('Âä†ËΩΩKÁ∫øÊï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // Âä†ËΩΩËµÑÈáëË¥πÁéáÊï∞ÊçÆ
    async loadFundingRateData(symbol, exchange, startDate, endDate) {
        console.log(`Âä†ËΩΩËµÑÈáëË¥πÁéáÊï∞ÊçÆ: ${symbol} ${exchange}`);

        try {
            // ÁîüÊàêÊ®°ÊãüÊï∞ÊçÆ
            const data = this.generateMockFundingRateData(symbol, startDate, endDate);
            return data;
        } catch (error) {
            console.error('Âä†ËΩΩËµÑÈáëË¥πÁéáÊï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // Âä†ËΩΩÁàÜ‰ªìÊï∞ÊçÆ
    async loadLiquidationData(symbol, exchange, startDate, endDate) {
        console.log(`Âä†ËΩΩÁàÜ‰ªìÊï∞ÊçÆ: ${symbol} ${exchange}`);

        try {
            const data = this.generateMockLiquidationData(symbol, startDate, endDate);
            return data;
        } catch (error) {
            console.error('Âä†ËΩΩÁàÜ‰ªìÊï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // Âä†ËΩΩÊåÅ‰ªìÊï∞ÊçÆ
    async loadOpenInterestData(symbol, exchange, startDate, endDate) {
        console.log(`Âä†ËΩΩÊåÅ‰ªìÊï∞ÊçÆ: ${symbol} ${exchange}`);

        try {
            const data = this.generateMockOpenInterestData(symbol, startDate, endDate);
            return data;
        } catch (error) {
            console.error('Âä†ËΩΩÊåÅ‰ªìÊï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // Âä†ËΩΩÂ§öÁ©∫ÊØîÊï∞ÊçÆ
    async loadLongShortRatioData(symbol, exchange, startDate, endDate) {
        console.log(`Âä†ËΩΩÂ§öÁ©∫ÊØîÊï∞ÊçÆ: ${symbol} ${exchange}`);

        try {
            const data = this.generateMockLongShortRatioData(symbol, startDate, endDate);
            return data;
        } catch (error) {
            console.error('Âä†ËΩΩÂ§öÁ©∫ÊØîÊï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // Âä†ËΩΩÊåáÊï∞Êï∞ÊçÆ
    async loadIndexData(indexType) {
        console.log(`Âä†ËΩΩÊåáÊï∞Êï∞ÊçÆ: ${indexType}`);

        try {
            const data = this.generateMockIndexData(indexType);
            return data;
        } catch (error) {
            console.error('Âä†ËΩΩÊåáÊï∞Êï∞ÊçÆÂ§±Ë¥•:', error);
            throw error;
        }
    }

    // ÊâπÈáèÂä†ËΩΩÊï∞ÊçÆ
    async batchLoadData(dataType, symbols, exchanges, timeframe, startDate, endDate) {
        console.log(`ÊâπÈáèÂä†ËΩΩ ${dataType} Êï∞ÊçÆ...`);

        const results = [];

        for (const symbol of symbols) {
            for (const exchange of exchanges) {
                try {
                    let data;

                    switch (dataType) {
                        case 'kline':
                            data = await this.loadKlineData(symbol, exchange, timeframe, startDate, endDate);
                            break;
                        case 'funding_rate':
                            data = await this.loadFundingRateData(symbol, exchange, startDate, endDate);
                            break;
                        case 'liquidation':
                            data = await this.loadLiquidationData(symbol, exchange, startDate, endDate);
                            break;
                        case 'open_interest':
                            data = await this.loadOpenInterestData(symbol, exchange, startDate, endDate);
                            break;
                        case 'long_short_ratio':
                            data = await this.loadLongShortRatioData(symbol, exchange, startDate, endDate);
                            break;
                        default:
                            console.warn(`‰∏çÊîØÊåÅÁöÑÊï∞ÊçÆÁ±ªÂûã: ${dataType}`);
                            continue;
                    }

                    results.push({
                        symbol,
                        exchange,
                        dataType,
                        data
                    });
                } catch (error) {
                    console.error(`Âä†ËΩΩ ${symbol} ${exchange} Â§±Ë¥•:`, error);
                }
            }
        }

        return results;
    }

    // Êï∞ÊçÆÈ¢ÑÂ§ÑÁêÜ
    preprocessData(rawData, dataType) {
        console.log(`È¢ÑÂ§ÑÁêÜ ${dataType} Êï∞ÊçÆ...`);

        switch (dataType) {
            case 'kline':
                return this.preprocessKlineData(rawData);
            case 'funding_rate':
                return this.preprocessFundingRateData(rawData);
            case 'liquidation':
                return this.preprocessLiquidationData(rawData);
            default:
                return rawData;
        }
    }

    // È¢ÑÂ§ÑÁêÜKÁ∫øÊï∞ÊçÆ
    preprocessKlineData(data) {
        return data.map(candle => ({
            ...candle,
            // ËÆ°ÁÆóÊäÄÊúØÊåáÊ†á
            sma20: this.calculateSMA(data, 20, data.indexOf(candle)),
            ema12: this.calculateEMA(data, 12, data.indexOf(candle)),
            rsi: this.calculateRSI(data, 14, data.indexOf(candle)),
            volume_sma: this.calculateVolumeSMA(data, 20, data.indexOf(candle))
        }));
    }

    // È¢ÑÂ§ÑÁêÜËµÑÈáëË¥πÁéáÊï∞ÊçÆ
    preprocessFundingRateData(data) {
        return data.map(item => ({
            ...item,
            // ËÆ°ÁÆóÁ¥ØËÆ°ËµÑÈáëË¥πÁéá
            cumulative_rate: this.calculateCumulativeRate(data, data.indexOf(item)),
            // ËÆ°ÁÆóÁßªÂä®Âπ≥Âùá
            ma_rate: this.calculateMARate(data, 7, data.indexOf(item))
        }));
    }

    // È¢ÑÂ§ÑÁêÜÁàÜ‰ªìÊï∞ÊçÆ
    preprocessLiquidationData(data) {
        return data.map(item => ({
            ...item,
            // ËÆ°ÁÆóÁàÜ‰ªìÂº∫Â∫¶
            liquidation_intensity: item.long_liquidation + item.short_liquidation,
            // ËÆ°ÁÆóÂ§öÁ©∫ÁàÜ‰ªìÊØî
            long_short_liq_ratio: item.long_liquidation / (item.short_liquidation || 1)
        }));
    }

    // ËÆ°ÁÆóÁÆÄÂçïÁßªÂä®Âπ≥Âùá
    calculateSMA(data, period, currentIndex) {
        if (currentIndex < period - 1) return null;

        let sum = 0;
        for (let i = currentIndex - period + 1; i <= currentIndex; i++) {
            sum += data[i].close;
        }
        return sum / period;
    }

    // ËÆ°ÁÆóÊåáÊï∞ÁßªÂä®Âπ≥Âùá
    calculateEMA(data, period, currentIndex) {
        if (currentIndex < period - 1) return null;

        const multiplier = 2 / (period + 1);
        let ema = data[currentIndex - period + 1].close;

        for (let i = currentIndex - period + 2; i <= currentIndex; i++) {
            ema = (data[i].close - ema) * multiplier + ema;
        }
        return ema;
    }

    // ËÆ°ÁÆóRSI
    calculateRSI(data, period, currentIndex) {
        if (currentIndex < period) return null;

        let gains = 0;
        let losses = 0;

        for (let i = currentIndex - period + 1; i <= currentIndex; i++) {
            const change = data[i].close - data[i - 1].close;
            if (change > 0) {
                gains += change;
            } else {
                losses -= change;
            }
        }

        const avgGain = gains / period;
        const avgLoss = losses / period;

        if (avgLoss === 0) return 100;

        const rs = avgGain / avgLoss;
        return 100 - (100 / (1 + rs));
    }

    // ËÆ°ÁÆóÊàê‰∫§ÈáèÁßªÂä®Âπ≥Âùá
    calculateVolumeSMA(data, period, currentIndex) {
        if (currentIndex < period - 1) return null;

        let sum = 0;
        for (let i = currentIndex - period + 1; i <= currentIndex; i++) {
            sum += data[i].volume;
        }
        return sum / period;
    }

    // ËÆ°ÁÆóÁ¥ØËÆ°ËµÑÈáëË¥πÁéá
    calculateCumulativeRate(data, currentIndex) {
        let cumulative = 0;
        for (let i = 0; i <= currentIndex; i++) {
            cumulative += data[i].rate;
        }
        return cumulative;
    }

    // ËÆ°ÁÆóËµÑÈáëË¥πÁéáÁßªÂä®Âπ≥Âùá
    calculateMARate(data, period, currentIndex) {
        if (currentIndex < period - 1) return null;

        let sum = 0;
        for (let i = currentIndex - period + 1; i <= currentIndex; i++) {
            sum += data[i].rate;
        }
        return sum / period;
    }

    // Êï∞ÊçÆÁªüËÆ°ÂàÜÊûê
    analyzeData(data, dataType) {
        console.log(`ÂàÜÊûê ${dataType} Êï∞ÊçÆ...`);

        const stats = {
            count: data.length,
            dataType: dataType,
            timeRange: {
                start: data[0]?.timestamp,
                end: data[data.length - 1]?.timestamp
            }
        };

        switch (dataType) {
            case 'kline':
                return this.analyzeKlineData(data, stats);
            case 'funding_rate':
                return this.analyzeFundingRateData(data, stats);
            case 'liquidation':
                return this.analyzeLiquidationData(data, stats);
            case 'open_interest':
                return this.analyzeOpenInterestData(data, stats);
            default:
                return stats;
        }
    }

    // ÂàÜÊûêKÁ∫øÊï∞ÊçÆ
    analyzeKlineData(data, stats) {
        const prices = data.map(d => d.close);
        const volumes = data.map(d => d.volume);

        return {
            ...stats,
            price: {
                min: Math.min(...prices),
                max: Math.max(...prices),
                avg: prices.reduce((a, b) => a + b, 0) / prices.length,
                last: prices[prices.length - 1],
                change: ((prices[prices.length - 1] - prices[0]) / prices[0]) * 100
            },
            volume: {
                min: Math.min(...volumes),
                max: Math.max(...volumes),
                avg: volumes.reduce((a, b) => a + b, 0) / volumes.length,
                total: volumes.reduce((a, b) => a + b, 0)
            },
            volatility: this.calculateVolatility(prices)
        };
    }

    // ÂàÜÊûêËµÑÈáëË¥πÁéáÊï∞ÊçÆ
    analyzeFundingRateData(data, stats) {
        const rates = data.map(d => d.rate);

        return {
            ...stats,
            fundingRate: {
                min: Math.min(...rates),
                max: Math.max(...rates),
                avg: rates.reduce((a, b) => a + b, 0) / rates.length,
                current: rates[rates.length - 1],
                positive_count: rates.filter(r => r > 0).length,
                negative_count: rates.filter(r => r < 0).length,
                cumulative: rates.reduce((a, b) => a + b, 0)
            }
        };
    }

    // ÂàÜÊûêÁàÜ‰ªìÊï∞ÊçÆ
    analyzeLiquidationData(data, stats) {
        const longLiqs = data.map(d => d.long_liquidation || 0);
        const shortLiqs = data.map(d => d.short_liquidation || 0);

        return {
            ...stats,
            liquidation: {
                total_long: longLiqs.reduce((a, b) => a + b, 0),
                total_short: shortLiqs.reduce((a, b) => a + b, 0),
                max_long: Math.max(...longLiqs),
                max_short: Math.max(...shortLiqs),
                avg_long: longLiqs.reduce((a, b) => a + b, 0) / longLiqs.length,
                avg_short: shortLiqs.reduce((a, b) => a + b, 0) / shortLiqs.length
            }
        };
    }

    // ÂàÜÊûêÊåÅ‰ªìÊï∞ÊçÆ
    analyzeOpenInterestData(data, stats) {
        const oi = data.map(d => d.open_interest);

        return {
            ...stats,
            openInterest: {
                min: Math.min(...oi),
                max: Math.max(...oi),
                avg: oi.reduce((a, b) => a + b, 0) / oi.length,
                current: oi[oi.length - 1],
                change: ((oi[oi.length - 1] - oi[0]) / oi[0]) * 100
            }
        };
    }

    // ËÆ°ÁÆóÊ≥¢Âä®Áéá
    calculateVolatility(prices) {
        if (prices.length < 2) return 0;

        const returns = [];
        for (let i = 1; i < prices.length; i++) {
            returns.push((prices[i] - prices[i - 1]) / prices[i - 1]);
        }

        const avgReturn = returns.reduce((a, b) => a + b, 0) / returns.length;
        const variance = returns.reduce((sum, r) => sum + Math.pow(r - avgReturn, 2), 0) / returns.length;

        return Math.sqrt(variance) * Math.sqrt(252) * 100; // Âπ¥ÂåñÊ≥¢Âä®Áéá
    }

    // ÂØºÂá∫Êï∞ÊçÆ
    exportData(data, format = 'json') {
        console.log(`ÂØºÂá∫Êï∞ÊçÆ‰∏∫ ${format} Ê†ºÂºè...`);

        switch (format) {
            case 'json':
                return JSON.stringify(data, null, 2);
            case 'csv':
                return this.convertToCSV(data);
            case 'parquet':
                // ÂÆûÈôÖÂÆûÁé∞ÈúÄË¶Å‰∏ìÈó®ÁöÑÂ∫ì
                console.log('ParquetÊ†ºÂºèÂØºÂá∫ÈúÄË¶ÅÈ¢ùÂ§ñÁöÑÂ∫ìÊîØÊåÅ');
                return data;
            default:
                return data;
        }
    }

    // ËΩ¨Êç¢‰∏∫CSVÊ†ºÂºè
    convertToCSV(data) {
        if (!data || data.length === 0) return '';

        const headers = Object.keys(data[0]);
        const csv = [
            headers.join(','),
            ...data.map(row => headers.map(h => row[h]).join(','))
        ].join('\n');

        return csv;
    }

    // ==================== Ê®°ÊãüÊï∞ÊçÆÁîüÊàê ====================

    generateMockKlineData(symbol, timeframe, startDate, endDate) {
        const data = [];
        const basePrice = symbol === 'BTCUSDT' ? 43000 : 2200;
        const start = new Date(startDate).getTime();
        const end = new Date(endDate).getTime();
        const interval = this.getIntervalMs(timeframe);

        for (let time = start; time <= end; time += interval) {
            const open = basePrice + (Math.random() - 0.5) * 1000;
            const close = open + (Math.random() - 0.5) * 500;
            const high = Math.max(open, close) + Math.random() * 200;
            const low = Math.min(open, close) - Math.random() * 200;
            const volume = Math.random() * 1000000;

            data.push({
                timestamp: new Date(time).toISOString(),
                open,
                high,
                low,
                close,
                volume
            });
        }

        return data;
    }

    generateMockFundingRateData(symbol, startDate, endDate) {
        const data = [];
        const start = new Date(startDate).getTime();
        const end = new Date(endDate).getTime();
        const interval = 8 * 60 * 60 * 1000; // 8Â∞èÊó∂

        for (let time = start; time <= end; time += interval) {
            data.push({
                timestamp: new Date(time).toISOString(),
                symbol,
                rate: (Math.random() - 0.5) * 0.002, // -0.1% to 0.1%
                next_funding_time: new Date(time + interval).toISOString()
            });
        }

        return data;
    }

    generateMockLiquidationData(symbol, startDate, endDate) {
        const data = [];
        const start = new Date(startDate).getTime();
        const end = new Date(endDate).getTime();
        const interval = 60 * 60 * 1000; // 1Â∞èÊó∂

        for (let time = start; time <= end; time += interval) {
            data.push({
                timestamp: new Date(time).toISOString(),
                symbol,
                long_liquidation: Math.random() * 10000000,
                short_liquidation: Math.random() * 10000000,
                total_liquidation: 0
            });
        }

        data.forEach(d => {
            d.total_liquidation = d.long_liquidation + d.short_liquidation;
        });

        return data;
    }

    generateMockOpenInterestData(symbol, startDate, endDate) {
        const data = [];
        const start = new Date(startDate).getTime();
        const end = new Date(endDate).getTime();
        const interval = 60 * 60 * 1000; // 1Â∞èÊó∂
        let oi = 1000000000; // 10‰∫øÁæéÂÖÉ

        for (let time = start; time <= end; time += interval) {
            oi = oi * (1 + (Math.random() - 0.5) * 0.02); // ¬±1%ÂèòÂåñ

            data.push({
                timestamp: new Date(time).toISOString(),
                symbol,
                open_interest: oi,
                open_interest_value: oi * 43000 // ÂÅáËÆæBTC‰ª∑Ê†º
            });
        }

        return data;
    }

    generateMockLongShortRatioData(symbol, startDate, endDate) {
        const data = [];
        const start = new Date(startDate).getTime();
        const end = new Date(endDate).getTime();
        const interval = 60 * 60 * 1000; // 1Â∞èÊó∂

        for (let time = start; time <= end; time += interval) {
            const ratio = 0.5 + Math.random(); // 0.5 Âà∞ 1.5

            data.push({
                timestamp: new Date(time).toISOString(),
                symbol,
                long_ratio: ratio / (1 + ratio),
                short_ratio: 1 / (1 + ratio),
                long_short_ratio: ratio
            });
        }

        return data;
    }

    generateMockIndexData(indexType) {
        const data = [];
        const now = Date.now();

        for (let i = 365; i >= 0; i--) {
            const time = now - i * 24 * 60 * 60 * 1000;

            switch (indexType) {
                case 'fear_greed':
                    data.push({
                        timestamp: new Date(time).toISOString(),
                        value: Math.floor(Math.random() * 100),
                        classification: this.getFearGreedClassification(Math.random() * 100)
                    });
                    break;
                case 'bubble_index':
                    data.push({
                        timestamp: new Date(time).toISOString(),
                        value: Math.random() * 5,
                        status: Math.random() > 0.7 ? 'bubble' : 'normal'
                    });
                    break;
                default:
                    data.push({
                        timestamp: new Date(time).toISOString(),
                        value: Math.random() * 100
                    });
            }
        }

        return data;
    }

    getFearGreedClassification(value) {
        if (value < 20) return 'Extreme Fear';
        if (value < 40) return 'Fear';
        if (value < 60) return 'Neutral';
        if (value < 80) return 'Greed';
        return 'Extreme Greed';
    }

    getIntervalMs(timeframe) {
        const intervals = {
            '1m': 60 * 1000,
            '5m': 5 * 60 * 1000,
            '15m': 15 * 60 * 1000,
            '1h': 60 * 60 * 1000,
            '4h': 4 * 60 * 60 * 1000,
            '1d': 24 * 60 * 60 * 1000,
            '1w': 7 * 24 * 60 * 60 * 1000,
            '1M': 30 * 24 * 60 * 60 * 1000
        };
        return intervals[timeframe] || intervals['1h'];
    }
}

// ÂØºÂá∫Ê®°Âùó
if (typeof module !== 'undefined' && module.exports) {
    module.exports = HistoricalDataLoader;
}

