/**
 * ÂéÜÂè≤Êï∞ÊçÆÊü•ÁúãÂô®ÂäüËÉΩÊµãËØïÂ•ó‰ª∂
 * ÈíàÂØπ historical-data-viewer.html È°µÈù¢ÁöÑÂÖ®Èù¢ÂäüËÉΩÊµãËØï
 */

class HistoricalDataTest {
    constructor() {
        this.testResults = [];
        this.currentTestIndex = 0;
        this.charts = {};
        this.testStartTime = null;
    }

    /**
     * ÂàùÂßãÂåñÊµãËØïÁéØÂ¢É
     */
    async initialize() {
        console.log('===== ÂéÜÂè≤Êï∞ÊçÆÊü•ÁúãÂô®ÂäüËÉΩÊµãËØïÂºÄÂßã =====');
        this.testStartTime = Date.now();

        // Á°Æ‰øùÈ°µÈù¢Â∑≤Âä†ËΩΩ
        await this.waitForPageLoad();

        // ÂàùÂßãÂåñÂõæË°®ÂÆû‰æã
        this.initializeCharts();

        return true;
    }

    /**
     * Á≠âÂæÖÈ°µÈù¢ÂÆåÂÖ®Âä†ËΩΩ
     */
    async waitForPageLoad() {
        return new Promise((resolve) => {
            if (document.readyState === 'complete') {
                resolve();
            } else {
                window.addEventListener('load', resolve);
            }
        });
    }

    /**
     * ÂàùÂßãÂåñÂõæË°®ÂÆû‰æãÁî®‰∫éÊµãËØï
     */
    initializeCharts() {
        // Ëé∑ÂèñÂõæË°®ÂÆπÂô®
        const mainChartContainer = document.getElementById('main-chart');
        const indicatorChartContainer = document.getElementById('indicator-chart');

        if (mainChartContainer && typeof LightweightCharts !== 'undefined') {
            this.charts.main = LightweightCharts.createChart(mainChartContainer, {
                width: mainChartContainer.clientWidth,
                height: 400,
                layout: {
                    backgroundColor: '#1a1a1a',
                    textColor: '#d4d4d4'
                },
                grid: {
                    vertLines: { color: '#2a2a2a' },
                    horzLines: { color: '#2a2a2a' }
                }
            });
        }

        if (indicatorChartContainer && typeof LightweightCharts !== 'undefined') {
            this.charts.indicator = LightweightCharts.createChart(indicatorChartContainer, {
                width: indicatorChartContainer.clientWidth,
                height: 200,
                layout: {
                    backgroundColor: '#1a1a1a',
                    textColor: '#d4d4d4'
                }
            });
        }
    }

    /**
     * ËøêË°åÊâÄÊúâÊµãËØï
     */
    async runAllTests() {
        const tests = [
            // UIÂÖÉÁ¥†ÊµãËØï
            { name: 'UIÂÖÉÁ¥†Âä†ËΩΩÊµãËØï', func: () => this.testUIElements() },
            { name: 'Êéß‰ª∂‰∫§‰∫íÊµãËØï', func: () => this.testControlInteractions() },

            // Êï∞ÊçÆÂä†ËΩΩÊµãËØï
            { name: 'KÁ∫øÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testKlineData() },
            { name: 'ËµÑÈáëË¥πÁéáÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testFundingRateData() },
            { name: 'ÁàÜ‰ªìÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testLiquidationData() },
            { name: 'ÊåÅ‰ªìÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testOpenInterestData() },
            { name: 'Â§öÁ©∫ÊØîÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testLongShortRatioData() },
            { name: 'ËÆ¢ÂçïÁ∞øÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testOrderbookData() },
            { name: 'Â∏ÇÂú∫Êï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testMarketData() },
            { name: 'ETFÊï∞ÊçÆÂä†ËΩΩÊµãËØï', func: () => this.testETFData() },

            // ÂõæË°®ÂäüËÉΩÊµãËØï
            { name: 'ÂõæË°®ÂàùÂßãÂåñÊµãËØï', func: () => this.testChartInitialization() },
            { name: 'ÂõæË°®ÂàáÊç¢ÊµãËØï', func: () => this.testChartSwitching() },
            { name: 'Êó∂Èó¥Âë®ÊúüÂàáÊç¢ÊµãËØï', func: () => this.testTimeframeSwitching() },
            { name: 'ÊäÄÊúØÊåáÊ†áÊµãËØï', func: () => this.testTechnicalIndicators() },

            // Êï∞ÊçÆÁ≠õÈÄâÊµãËØï
            { name: 'Êó•ÊúüËåÉÂõ¥Á≠õÈÄâÊµãËØï', func: () => this.testDateRangeFilter() },
            { name: '‰∫§ÊòìÊâÄÁ≠õÈÄâÊµãËØï', func: () => this.testExchangeFilter() },
            { name: '‰∫§ÊòìÂØπÁ≠õÈÄâÊµãËØï', func: () => this.testSymbolFilter() },

            // ÂØºÂá∫ÂäüËÉΩÊµãËØï
            { name: 'Êï∞ÊçÆÂØºÂá∫CSVÊµãËØï', func: () => this.testExportCSV() },
            { name: 'Êï∞ÊçÆÂØºÂá∫JSONÊµãËØï', func: () => this.testExportJSON() },
            { name: 'ÂõæË°®Êà™ÂõæÊµãËØï', func: () => this.testChartScreenshot() },

            // ÊÄßËÉΩÊµãËØï
            { name: 'Â§ßÊï∞ÊçÆÈáèÂä†ËΩΩÊµãËØï', func: () => this.testLargeDataLoad() },
            { name: 'ÂÜÖÂ≠ò‰ΩøÁî®ÊµãËØï', func: () => this.testMemoryUsage() },

            // ÈîôËØØÂ§ÑÁêÜÊµãËØï
            { name: 'ÁΩëÁªúÈîôËØØÂ§ÑÁêÜÊµãËØï', func: () => this.testNetworkError() },
            { name: 'Êï∞ÊçÆÊ†ºÂºèÈîôËØØÂ§ÑÁêÜÊµãËØï', func: () => this.testDataFormatError() }
        ];

        console.log(`ÂºÄÂßãÊâßË°å ${tests.length} ‰∏™ÊµãËØïÁî®‰æã...`);

        for (const test of tests) {
            this.currentTestIndex++;
            console.log(`\n[${this.currentTestIndex}/${tests.length}] ÊâßË°å: ${test.name}`);

            try {
                const result = await test.func();
                this.testResults.push({
                    name: test.name,
                    success: result.success,
                    message: result.message,
                    duration: result.duration || 0
                });

                if (result.success) {
                    console.log(`‚úÖ ÈÄöËøá: ${result.message}`);
                } else {
                    console.error(`‚ùå Â§±Ë¥•: ${result.message}`);
                }
            } catch (error) {
                this.testResults.push({
                    name: test.name,
                    success: false,
                    message: `ÊµãËØïÂºÇÂ∏∏: ${error.message}`,
                    duration: 0
                });
                console.error(`‚ùå ÂºÇÂ∏∏: ${error.message}`);
            }

            // ÊµãËØïÈó¥Èöî
            await this.delay(500);
        }

        return this.generateReport();
    }

    /**
     * UIÂÖÉÁ¥†Âä†ËΩΩÊµãËØï
     */
    async testUIElements() {
        const startTime = Date.now();
        const elements = [
            { id: 'dataTypeSelect', name: 'Êï∞ÊçÆÁ±ªÂûãÈÄâÊã©Âô®' },
            { id: 'exchangeSelect', name: '‰∫§ÊòìÊâÄÈÄâÊã©Âô®' },
            { id: 'symbolSelect', name: '‰∫§ÊòìÂØπÈÄâÊã©Âô®' },
            { id: 'intervalSelect', name: 'Êó∂Èó¥Âë®ÊúüÈÄâÊã©Âô®' },
            { id: 'startDate', name: 'ÂºÄÂßãÊó•ÊúüËæìÂÖ•' },
            { id: 'endDate', name: 'ÁªìÊùüÊó•ÊúüËæìÂÖ•' },
            { id: 'loadDataBtn', name: 'Âä†ËΩΩÊï∞ÊçÆÊåâÈíÆ' },
            { id: 'main-chart', name: '‰∏ªÂõæË°®ÂÆπÂô®' },
            { id: 'indicator-chart', name: 'ÊåáÊ†áÂõæË°®ÂÆπÂô®' },
            { id: 'data-table', name: 'Êï∞ÊçÆË°®Ê†ºÂÆπÂô®' }
        ];

        let missingElements = [];
        for (const elem of elements) {
            if (!document.getElementById(elem.id)) {
                missingElements.push(elem.name);
            }
        }

        const success = missingElements.length === 0;
        return {
            success,
            message: success 
                ? 'ÊâÄÊúâUIÂÖÉÁ¥†Â∑≤Ê≠£Á°ÆÂä†ËΩΩ' 
                : `Áº∫Â§±ÂÖÉÁ¥†: ${missingElements.join(', ')}`,
            duration: Date.now() - startTime
        };
    }

    /**
     * Êéß‰ª∂‰∫§‰∫íÊµãËØï
     */
    async testControlInteractions() {
        const startTime = Date.now();

        try {
            // ÊµãËØïÊï∞ÊçÆÁ±ªÂûãÈÄâÊã©Âô®
            const dataTypeSelect = document.getElementById('dataTypeSelect');
            if (dataTypeSelect) {
                dataTypeSelect.value = 'funding_rate';
                dataTypeSelect.dispatchEvent(new Event('change'));
                await this.delay(100);
            }

            // ÊµãËØï‰∫§ÊòìÊâÄÈÄâÊã©Âô®
            const exchangeSelect = document.getElementById('exchangeSelect');
            if (exchangeSelect) {
                exchangeSelect.value = 'OKX';
                exchangeSelect.dispatchEvent(new Event('change'));
                await this.delay(100);
            }

            // ÊµãËØïÊåâÈíÆÁÇπÂáª
            const loadBtn = document.getElementById('loadDataBtn');
            if (loadBtn) {
                loadBtn.click();
                await this.delay(100);
            }

            return {
                success: true,
                message: 'Êéß‰ª∂‰∫§‰∫íÊ≠£Â∏∏',
