    /**
     * ÁîüÊàêÊµãËØïÊä•Âëä
     */
    generateReport() {
        const totalTests = this.testResults.length;
        const passedTests = this.testResults.filter(r => r.success).length;
        const failedTests = totalTests - passedTests;
        const totalDuration = Date.now() - this.testStartTime;
        const successRate = (passedTests / totalTests * 100).toFixed(2);

        const report = {
            summary: {
                totalTests,
                passed: passedTests,
                failed: failedTests,
                successRate: `${successRate}%`,
                duration: `${(totalDuration / 1000).toFixed(2)}s`
            },
            details: this.testResults,
            timestamp: new Date().toISOString()
        };

        console.log('\n===== ÊµãËØïÊä•Âëä =====');
        console.log(`ÊÄªÊµãËØïÊï∞: ${totalTests}`);
        console.log(`‚úÖ ÈÄöËøá: ${passedTests}`);
        console.log(`‚ùå Â§±Ë¥•: ${failedTests}`);
        console.log(`ÊàêÂäüÁéá: ${successRate}%`);
        console.log(`ÊÄªËÄóÊó∂: ${(totalDuration / 1000).toFixed(2)}Áßí`);
        console.log('==================\n');

        // ÊòæÁ§∫Â§±Ë¥•ÁöÑÊµãËØï
        if (failedTests > 0) {
            console.log('Â§±Ë¥•ÁöÑÊµãËØï:');
            this.testResults
                .filter(r => !r.success)
                .forEach(r => console.log(`  ‚ùå ${r.name}: ${r.message}`));
        }

        return report;
    }
