    generateMockKlineData() {
        const data = [];
        const now = Date.now();

        for (let i = 100; i >= 0; i--) {
            const basePrice = 50000 + Math.sin(i / 10) * 5000;
            data.push({
                time: now - i * 3600000,
                open: basePrice + Math.random() * 100,
                high: basePrice + Math.random() * 200 + 100,
                low: basePrice - Math.random() * 200,
                close: basePrice + Math.random() * 100,
                volume: Math.random() * 1000000
            });
        }

        return data;
    }

