// Êõ¥Êñ∞ÁªüËÆ°‰ø°ÊÅØ
function updateStatistics(analysis) {
    if (analysis.price) {
        document.getElementById('currentPrice').textContent = 
            '$' + analysis.price.last.toFixed(2);
        document.getElementById('change24h').textContent = 
            (analysis.price.change >= 0 ? '+' : '') + analysis.price.change.toFixed(2) + '%';
        document.getElementById('change24h').style.color = 
            analysis.price.change >= 0 ? 'var(--bull-color)' : 'var(--bear-color)';
    }

    if (analysis.volume) {
        document.getElementById('volume24h').textContent = 
            formatVolume(analysis.volume.total);
    }

    if (analysis.volatility) {
        document.getElementById('volatility').textContent = 
            analysis.volatility.toFixed(2) + '%';
    }

    if (analysis.fundingRate) {
        document.getElementById('fundingRate').textContent = 
            (analysis.fundingRate.current * 100).toFixed(4) + '%';
        document.getElementById('fundingRate').style.color = 
            analysis.fundingRate.current >= 0 ? 'var(--bull-color)' : 'var(--bear-color)';
    }
}

// Êõ¥Êñ∞Êï∞ÊçÆÁªüËÆ°
function updateDataStats(data) {
    document.getElementById('dataPoints').textContent = data.length;

    if (data.length > 0) {
        const start = new Date(data[0].timestamp);
        const end = new Date(data[data.length - 1].timestamp);
        const days = Math.floor((end - start) / (1000 * 60 * 60 * 24));
        document.getElementById('timeSpan').textContent = days + ' Â§©';
    }

    document.getElementById('updateTime').textContent = 
        new Date().toLocaleTimeString();
}

