// ÊòæÁ§∫ËÆ¢ÂçïÁ∞øÊï∞ÊçÆ
function displayOrderbookData(data) {
    if (!mainChart) return;

    // Ê∏ÖÁêÜÁé∞ÊúâÂõæË°®
    if (mainChart._series) {
        mainChart._series.forEach(s => mainChart.removeSeries(s));
    }
    mainChart._series = [];

    // ÂàõÂª∫‰π∞ÂçïÊï∞ÊçÆ
    const bidSeries = mainChart.addHistogramSeries({
        color: '#10B981',
        priceFormat: {
            type: 'volume',
        },
        priceScaleId: 'right',
    });

    const bidData = data.bids ? data.bids.map((bid, index) => ({
        time: Date.now() / 1000 - (data.bids.length - index) * 60,
        value: bid.amount || bid.total || Math.random() * 100
    })) : [];

    bidSeries.setData(bidData);

    // ÂàõÂª∫ÂçñÂçïÊï∞ÊçÆ
    const askSeries = mainChart.addHistogramSeries({
        color: '#EF4444',
        priceFormat: {
            type: 'volume',
        },
        priceScaleId: 'right',
    });

    const askData = data.asks ? data.asks.map((ask, index) => ({
        time: Date.now() / 1000 - (data.asks.length - index) * 60,
        value: -(ask.amount || ask.total || Math.random() * 100)
    })) : [];

    askSeries.setData(askData);

    mainChart._series = [bidSeries, askSeries];
    mainChart.timeScale().fitContent();
}

// ÊòæÁ§∫ETFÊï∞ÊçÆ
function displayETFData(data) {
    if (!mainChart) return;

    // Ê∏ÖÁêÜÁé∞ÊúâÂõæË°®
    if (mainChart._series) {
        mainChart._series.forEach(s => mainChart.removeSeries(s));
    }
    mainChart._series = [];

    // ÂáÄÊµÅÂÖ•Êï∞ÊçÆ
    const flowSeries = mainChart.addHistogramSeries({
        color: '#6366F1',
        priceFormat: {
            type: 'volume',
        },
        priceScaleId: 'right',
    });

    const flowData = data.map ? data.map(d => ({
        time: new Date(d.date || d.timestamp || Date.now()).getTime() / 1000,
        value: d.netFlow || d.net_flow || (Math.random() - 0.5) * 100000000,
        color: (d.netFlow || d.net_flow || 0) >= 0 ? '#10B981' : '#EF4444'
    })) : [];

    flowSeries.setData(flowData);

    // ÊÄªËµÑ‰∫ßÊõ≤Á∫ø
    const assetSeries = mainChart.addLineSeries({
        color: '#F59E0B',
        lineWidth: 2,
        priceScaleId: 'left',
    });

    const assetData = data.map ? data.map(d => ({
        time: new Date(d.date || d.timestamp || Date.now()).getTime() / 1000,
        value: d.totalAssets || d.total_assets || 10000000000 + Math.random() * 1000000000
    })) : [];

    assetSeries.setData(assetData);

    mainChart._series = [flowSeries, assetSeries];
    mainChart.timeScale().fitContent();
}

// ÊòæÁ§∫Â∏ÇÂú∫Êï∞ÊçÆ
function displayMarketData(data) {
    if (!mainChart) return;

    // Ê∏ÖÁêÜÁé∞ÊúâÂõæË°®
    if (mainChart._series) {
        mainChart._series.forEach(s => mainChart.removeSeries(s));
    }
    mainChart._series = [];

    // Â∏ÇÂÄºÊï∞ÊçÆ
    const marketCapSeries = mainChart.addAreaSeries({
        topColor: 'rgba(99, 102, 241, 0.56)',
        bottomColor: 'rgba(99, 102, 241, 0.04)',
        lineColor: '#6366F1',
        lineWidth: 2,
    });

    const marketCapData = [{
        time: Date.now() / 1000,
        value: data.marketCap || data.market_cap || 1000000000000
    }];

    marketCapSeries.setData(marketCapData);

    // ÊòæÁ§∫ÂÖ∂‰ªñÂ∏ÇÂú∫ÊåáÊ†á
    if (indicatorChart) {
        // Ê∏ÖÁêÜÊåáÊ†áÂõæË°®
        if (indicatorChart._series) {
            indicatorChart._series.forEach(s => indicatorChart.removeSeries(s));
        }

        // ÊÅêÊÉßË¥™Â©™ÊåáÊï∞
        const fearGreedSeries = indicatorChart.addLineSeries({
            color: '#F59E0B',
            lineWidth: 2,
        });

        fearGreedSeries.setData([{
            time: Date.now() / 1000,
            value: data.fearGreedIndex || data.fear_greed_index || 50
        }]);
    }

    mainChart._series = [marketCapSeries];
    mainChart.timeScale().fitContent();
}

