    pub backtest_client: BacktestServiceClient<Channel>,
    pub execution_client: Option<ExecutionServiceClient<Channel>>,
    pub factor_client: Option<FactorServiceClient<Channel>>,
    pub risk_client: Option<RiskServiceClient<Channel>>,
    pub markets_client: Option<MarketDataServiceClient<Channel>>,
    pub data_management_client: Option<DataManagementServiceClient<Channel>>,
    pub health_status: Arc<RwLock<HashMap<String, ServiceHealthStatus>>>,
    pub idempotency_manager: IdempotencyManager,
}

#[derive(Clone, serde::Serialize, serde::Deserialize)]
pub struct ServiceHealthStatus {
    pub service: String,
    pub status: String,
    pub last_check: i64,
    pub latency_ms: Option<u64>,
}

