```javascript
// trading-platform-server.js
import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import axios from 'axios';

class TradingPlatformMCP extends Server {
  constructor() {
    super(
      {
        name: 'trading-platform-mcp',
        version: '1.0.0',
      },
      {
        capabilities: {
          tools: {},
          resources: {},
        },
      }
    );

    // 定义工具
    this.addTool({
      name: 'place_order',
      description: '下单交易',
      inputSchema: {
        type: 'object',
        properties: {
          symbol: { type: 'string' },
          side: { type: 'string', enum: ['BUY', 'SELL'] },
          quantity: { type: 'number' },
          order_type: { type: 'string', enum: ['MARKET', 'LIMIT'] },
          price: { type: 'number' }
        }
      }
    });

    this.addTool({
      name: 'get_market_data',
      description: '获取市场数据',
      inputSchema: {
        type: 'object',
        properties: {
          symbol: { type: 'string' },
          interval: { type: 'string' },
          limit: { type: 'number' }
        }
      }
    });

    this.addTool({
      name: 'run_backtest',
      description: '运行策略回测',
      inputSchema: {
        type: 'object',
        properties: {
          strategy_config: { type: 'object' },
          symbol: { type: 'string' },
          start_date: { type: 'string' },
          end_date: { type: 'string' }
        }
      }
    });
  }

  async handleCall(name, args) {
    const gatewayUrl = process.env.GATEWAY_URL || 'http://localhost:8080';

    switch (name) {
      case 'place_order':
        return await this.placeOrder(gatewayUrl, args);
      case 'get_market_data':
        return await this.getMarketData(gatewayUrl, args);
      case 'run_backtest':
        return await this.runBacktest(gatewayUrl, args);
    }
  }

  async placeOrder(gatewayUrl, args) {
    try {
      const response = await axios.post(`${gatewayUrl}/api/v1/orders`, args);
      return {
        content: [{
          type: 'text',
          text: `Order placed successfully: ${JSON.stringify(response.data)}`
        }]
      };
    } catch (error) {
      return {
        content: [{
          type: 'text',
          text: `Order failed: ${error.message}`
        }],
        isError: true
      };
    }
  }

  async getMarketData(gatewayUrl, args) {
    try {
      const response = await axios.get(`${gatewayUrl}/api/v1/markets/${args.symbol}/candles`, {
        params: { interval: args.interval, limit: args.limit }
      });
      return {
        content: [{
          type: 'text',
          text: `Market data: ${JSON.stringify(response.data, null, 2)}`
        }]
      };
    } catch (error) {
      return {
        content: [{
          type: 'text',
          text: `Market data fetch failed: ${error.message}`
        }],
        isError: true
      };
    }
  }

  async runBacktest(gatewayUrl, args) {
    try {
      const response = await axios.post(`${gatewayUrl}/api/v1/backtests`, {
        name: `Backtest-${Date.now()}`,
        strategy_config: args.strategy_config,
        symbol: args.symbol,
        start_date: args.start_date,
        end_date: args.end_date,
        initial_capital: 10000.0
      });

      return {
        content: [{
          type: 'text',
          text: `Backtest submitted: ${JSON.stringify(response.data)}`
        }]
      };
    } catch (error) {
      return {
        content: [{
          type: 'text',
          text: `Backtest failed: ${error.message}`
        }],
        isError: true
      };
    }
  }
}

// 启动服务器
async function main() {
  const server = new TradingPlatformMCP();
  const transport = new StdioServerTransport();
  await server.connect(transport);
}

main().catch(console.error);
```

