'use client'

import { useState, useTransition } from 'react'
import { useRouter } from 'next/navigation'
import { useSession } from 'next-auth/react'
import { Button } from '@/components/ui/button'
import { ShoppingCart } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'

interface CartActionsProps {
  productId: string
  stockQuantity: number
  quantity?: number
  disabled?: boolean
  className?: string
  compact?: boolean
}

export function CartActions({ 
  productId, 
  stockQuantity, 
  quantity = 1, 
  disabled = false,
  className = "",
  compact = false 
}: CartActionsProps) {
  const [isPending, startTransition] = useTransition()
  const router = useRouter()
  const { data: session } = useSession()
  const { toast } = useToast()

  const handleAddToCart = async () => {
    if (stockQuantity <= 0) {
      toast({
        title: "Out of stock",
        description: "This item is currently out of stock",
        variant: "destructive"
      })
      return
    }

    startTransition(async () => {
      try {
        const response = await fetch('/api/cart', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ 
            productId, 
            quantity 
          })
        })

        const data = await response.json()

        if (data.success) {
          // Trigger cart update event to update header count
          window.dispatchEvent(new Event('cart-updated'))
        } else {
          toast({
            title: "Error",
            description: data.error || "Failed to add item to cart",
            variant: "destructive"
          })
        }
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to add item to cart",
          variant: "destructive"
        })
      }
    })
  }

  if (compact) {
    return (
      <Button
        onClick={handleAddToCart}
        disabled={isPending || disabled || stockQuantity <= 0}
        className={`h-11 w-11 p-0 ${className}`}
        variant="outline"
      >
        <ShoppingCart className="h-4 w-4" />
      </Button>
    )
  }

  return (
    <Button
      onClick={handleAddToCart}
      disabled={isPending || disabled || stockQuantity <= 0}
      className={className}
    >
      <ShoppingCart className="h-4 w-4 mr-2" />
      {stockQuantity > 0 ? "Add to Cart" : "Out of Stock"}
    </Button>
  )
}

import { Suspense } from "react"
import ProductsContent from "./products-content"

export default function ProductsPage() {
  return (
    <Suspense fallback={
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-amber-50 flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-orange-600"></div>
          <p className="mt-4 text-orange-600">Loading products...</p>
        </div>
      </div>
    }>
      <ProductsContent />
    </Suspense>
  )
}

"use client"

import { useState, useEffect } from "react"
import Link from "next/link"
import { useSearchParams } from "next/navigation"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { WishlistActions } from "@/components/wishlist-actions"
import { CartActions } from "@/components/cart-actions"
import {
  Star,
  Filter,
  Grid,
  List,
  Search,
  ChevronLeft,
  ChevronRight,
} from "lucide-react"

type Product = {
  id: string
  name: string
  slug: string
  price: number
  comparePrice?: number
  images: { url: string; alt?: string; isPrimary: boolean }[]
  category: { name: string; slug: string }
  brand?: { name: string; slug: string }
  _count: { reviews: number }
  stockQuantity: number
  featured: boolean
}

type ApiResponse = {
  products: Product[]
  pagination: {
    page: number
    limit: number
    total: number
    totalPages: number
    hasNextPage: boolean
    hasPrevPage: boolean
  }
  filters: {
    category?: string
    minPrice?: string
    maxPrice?: string
    search?: string
    sort: string
    featured?: string
  }
}

const categories = [
  { name: "All Products", value: "" },
  { name: "Smart Lighting", value: "smart-lighting" },
  { name: "Security & Sensors", value: "security-sensors" },
  { name: "Smart Switches", value: "smart-switches" },
  { name: "Smart Hubs", value: "smart-hubs" },
]

const sortOptions = [
  { name: "Newest", value: "newest" },
  { name: "Oldest", value: "oldest" },
  { name: "Price: Low to High", value: "priceAsc" },
  { name: "Price: High to Low", value: "priceDesc" },
  { name: "Name A-Z", value: "name" },
]

export default function ProductsContent() {
  const searchParams = useSearchParams()
  const [apiResponse, setApiResponse] = useState<ApiResponse | null>(null)
  const [loading, setLoading] = useState(true)
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid")
  const [selectedCategory, setSelectedCategory] = useState("")
  const [selectedSort, setSelectedSort] = useState("newest")
  const [searchTerm, setSearchTerm] = useState("")
  const [priceRange, setPriceRange] = useState({ min: "", max: "" })
  const [showFilters, setShowFilters] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)

  useEffect(() => {
    fetchProducts()
  }, [selectedCategory, selectedSort, searchTerm, priceRange, currentPage])

  const fetchProducts = async () => {
    setLoading(true)
    try {
      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: "12",
        sort: selectedSort,
      })

      if (selectedCategory) params.append("category", selectedCategory)
      if (searchTerm.trim()) params.append("search", searchTerm.trim())
      if (priceRange.min) params.append("minPrice", priceRange.min)
      if (priceRange.max) params.append("maxPrice", priceRange.max)

      const response = await fetch(`/api/products?${params}`)
      if (!response.ok) {
        throw new Error("Failed to fetch products")
      }

      const data: ApiResponse = await response.json()
      setApiResponse(data)
    } catch (error) {
      console.error("Error fetching products:", error)
      setApiResponse(null)
    } finally {
      setLoading(false)
    }
  }

  const handleSearch = () => {
    setCurrentPage(1) // Reset to first page when searching
    fetchProducts()
  }

  const handlePriceFilter = () => {
    setCurrentPage(1) // Reset to first page when filtering
    fetchProducts()
  }

  const products = apiResponse?.products || []
  const pagination = apiResponse?.pagination

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-amber-50">
      {/* Page Header */}
      <div className="bg-gradient-to-r from-orange-100 to-amber-100 border-b border-orange-200">
        <div className="container mx-auto px-4 py-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <h1 className="text-3xl font-bold mb-2 text-orange-900">All Products</h1>
              <p className="text-orange-700">
                {pagination
                  ? `Showing ${products.length} of ${pagination.total} products`
                  : "Discover our complete range of smart home solutions"}
              </p>
            </div>
            <div className="mt-4 md:mt-0 flex items-center gap-4">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowFilters(!showFilters)}
                className="md:hidden"
              >
                <Filter className="h-4 w-4 mr-2" />
                Filters
              </Button>
              <div className="flex items-center gap-2">
                <Button
                  variant={viewMode === "grid" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("grid")}
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("list")}
                >
                  <List className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        <div className="flex gap-8">
          {/* Sidebar Filters */}
          <aside
            className={`${
              showFilters ? "block" : "hidden"
            } md:block w-full md:w-64 shrink-0`}
          >
            <div className="space-y-6">
              {/* Search */}
              <div>
                <h3 className="font-semibold mb-3 text-orange-900">Search</h3>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-orange-400" />
                  <Input
                    type="search"
                    placeholder="Search products..."
                    className="pl-10 border-orange-200 focus:border-orange-400 focus:ring-orange-400"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && handleSearch()}
                  />
                </div>
              </div>

              {/* Categories */}
              <div>
                <h3 className="font-semibold mb-3 text-orange-900">Categories</h3>
                <div className="space-y-2">
                  {categories.map((category) => (
                    <button
                      key={category.value}
                      onClick={() => setSelectedCategory(category.value)}
                      className={`w-full text-left px-3 py-2 rounded-md transition-colors ${
                        selectedCategory === category.value
                          ? "bg-orange-100 text-orange-700 font-medium"
                          : "hover:bg-orange-50"
                      }`}
                    >
                      {category.name}
                    </button>
                  ))}
                </div>
              </div>

              {/* Price Range */}
              <div>
                <h3 className="font-semibold mb-3 text-orange-900">Price Range</h3>
                <div className="space-y-3">
                  <div className="flex items-center gap-2">
                    <Input
                      type="number"
                      placeholder="Min"
                      value={priceRange.min}
                      onChange={(e) =>
                        setPriceRange({ ...priceRange, min: e.target.value })
                      }
                      onKeyDown={(e) => e.key === "Enter" && handlePriceFilter()}
                      className="w-full"
                    />
                    <span className="text-orange-500">-</span>
                    <Input
                      type="number"
                      placeholder="Max"
                      value={priceRange.max}
                      onChange={(e) =>
                        setPriceRange({ ...priceRange, max: e.target.value })
                      }
                      onKeyDown={(e) => e.key === "Enter" && handlePriceFilter()}
                      className="w-full"
                    />
                  </div>
                  <Button 
                    onClick={handlePriceFilter}
                    size="sm" 
                    className="w-full mt-2 bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600"
                  >
                    Apply Filter
                  </Button>
                </div>
              </div>

              {/* Sort */}
              <div>
                <h3 className="font-semibold mb-3 text-orange-900">Sort By</h3>
                <select
                  value={selectedSort}
                  onChange={(e) => setSelectedSort(e.target.value)}
                  className="w-full px-3 py-2 border border-orange-200 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
                >
                  {sortOptions.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </aside>

          {/* Products Grid/List */}
          <main className="flex-1">
            {loading ? (
              <div className="text-center py-12">
                <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-orange-600"></div>
              </div>
            ) : products.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-orange-500">No products found</p>
              </div>
            ) : (
              <div
                className={
                  viewMode === "grid"
                    ? "grid md:grid-cols-2 lg:grid-cols-3 gap-6 items-stretch"
                    : "space-y-4"
                }
              >
                {products.map((product) => (
                  <div key={product.id} className="h-full">
                    <Card className="group hover:shadow-xl transition-all duration-300 hover:scale-105 hover:-translate-y-2 h-full border border-orange-100 hover:border-orange-200 bg-white rounded-2xl flex flex-col">
                      {viewMode === "grid" ? (
                        <>
                          <CardHeader className="p-0">
                            <Link href={`/product/${product.slug}`} className="cursor-pointer">
                              <div className="relative h-48 bg-gradient-to-br from-orange-100 to-amber-100 group-hover:from-orange-200 group-hover:to-amber-200 transition-all duration-300">
                                {product.images.length > 0 ? (
                                  <img
                                    src={product.images[0].url}
                                    alt={product.images[0].alt || product.name}
                                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                                  />
                                ) : (
                                  <div className="h-full flex items-center justify-center text-orange-400">
                                    [No Image]
                                  </div>
                                )}
                                {product.comparePrice && (
                                  <span className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 text-xs rounded">
                                    {Math.round(
                                      ((product.comparePrice - product.price) /
                                        product.comparePrice) *
                                        100
                                    )}
                                    % OFF
                                  </span>
                                )}
                              </div>
                            </Link>
                          </CardHeader>
                          <CardContent className="p-4 flex-1 flex flex-col">
                            <p className="text-xs text-orange-500 mb-1">
                              {product.category.name}
                            </p>
                            <Link href={`/product/${product.slug}`} className="group-hover:text-orange-600 transition-colors duration-300">
                              <h3 className="font-semibold mb-2 text-orange-900 group-hover:text-orange-600 transition-colors duration-300 line-clamp-2">{product.name}</h3>
                            </Link>
                            <div className="flex items-center gap-1 mb-2">
                              <div className="flex">
                                {[...Array(5)].map((_, i) => (
                                  <Star
                                    key={i}
                                    className="h-4 w-4 text-orange-300"
                                  />
                                ))}
                              </div>
                              <span className="text-sm text-orange-600">
                                ({product._count.reviews} reviews)
                              </span>
                            </div>

                            {/* Flexible spacer to push price and stock info to bottom */}
                            <div className="flex-1"></div>

                            {/* Stock info and price at bottom */}
                            <div className="mt-auto">
                              {product.stockQuantity <= 5 && product.stockQuantity > 0 && (
                                <p className="text-sm text-orange-600 mb-2">
                                  Only {product.stockQuantity} left in stock
                                </p>
                              )}
                              {product.stockQuantity === 0 && (
                                <p className="text-sm text-red-600 mb-2">Out of stock</p>
                              )}
                              <div className="flex items-center gap-2">
                                <span className="text-xl font-bold text-orange-900">
                                  NZ${product.price.toFixed(2)}
                                </span>
                                {product.comparePrice && (
                                  <span className="text-sm text-orange-500 line-through">
                                    NZ${product.comparePrice.toFixed(2)}
                                  </span>
                                )}
                              </div>
                            </div>
                          </CardContent>
                          <CardFooter className="p-4 pt-0 mt-auto">
                            {/* 三个按钮水平排列，顶部对齐，相同间隔 */}
                            <div className="flex items-start gap-3 w-full">
                              {/* Add to Cart 按钮 */}
                              <CartActions 
                                productId={product.id}
                                stockQuantity={product.stockQuantity}
                                className="flex-1 h-11 bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600 text-white shadow-md font-semibold transition-all duration-300 hover:scale-105 hover:shadow-xl hover:shadow-orange-500/30 cursor-pointer text-sm"
                              />

                              {/* Like 按钮 */}
                              <div className="flex-shrink-0">
                                <WishlistActions productId={product.id} compact={true} />
                              </div>

                              {/* View Details 按钮 */}
                              <Link href={`/product/${product.slug}`} className="flex-1 cursor-pointer">
                                <Button variant="outline" className="w-full h-11 text-sm border-orange-200 text-orange-700 hover:bg-orange-50 hover:border-orange-300 transition-all duration-300 hover:scale-105 hover:shadow-md hover:shadow-orange-200/50 cursor-pointer">
                                  View Details
                                </Button>
                              </Link>
                            </div>
                          </CardFooter>
                        </>
                      ) : (
                        <div className="flex gap-4 p-4">
                          <div className="w-32 h-32 bg-gradient-to-br from-orange-100 to-amber-100 rounded-md flex-shrink-0">
                            {product.images.length > 0 ? (
                              <img
                                src={product.images[0].url}
                                alt={product.images[0].alt || product.name}
                                className="w-full h-full object-cover rounded-md"
                              />
                            ) : (
                              <div className="h-full flex items-center justify-center text-orange-400">
                                [No Image]
                              </div>
                            )}
                          </div>
                          <div className="flex-1">
                            <p className="text-xs text-orange-500 mb-1">
                              {product.category.name}
                            </p>
                            <Link href={`/product/${product.slug}`} className="group-hover:text-orange-600 transition-colors duration-300">
                              <h3 className="font-semibold mb-2 text-orange-900 group-hover:text-orange-600 transition-colors duration-300">{product.name}</h3>
                            </Link>
                            <div className="flex items-center gap-1 mb-2">
                              <div className="flex">
                                {[...Array(5)].map((_, i) => (
                                  <Star
                                    key={i}
                                    className="h-4 w-4 text-orange-300"
                                  />
                                ))}
                              </div>
                              <span className="text-sm text-orange-600">
                                ({product._count.reviews} reviews)
                              </span>
                            </div>
                            <div className="flex items-center justify-between mt-4">
                              <div className="flex items-center gap-2">
                                <span className="text-xl font-bold text-orange-900">
                                  ${product.price}
                                </span>
                                {product.comparePrice && (
                                  <span className="text-sm text-orange-500 line-through">
                                    ${product.comparePrice}
                                  </span>
                                )}
                              </div>
                              <div className="flex gap-2">
                                <Button className="bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600">Add to Cart</Button>
                                <WishlistActions productId={product.id} compact={true} />
                              </div>
                            </div>
                          </div>
                        </div>
                      )}
                    </Card>
                  </div>
                ))}
              </div>
            )}

            {/* Pagination */}
            {pagination && pagination.totalPages > 1 && (
              <div className="flex items-center justify-between mt-8">
                <div className="text-sm text-orange-600">
                  Page {pagination.page} of {pagination.totalPages}
                </div>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    className="border-orange-300 text-orange-700 hover:bg-orange-50"
                    size="sm"
                    disabled={!pagination.hasPrevPage}
                    onClick={() => setCurrentPage(currentPage - 1)}
                  >
                    <ChevronLeft className="h-4 w-4" />
                    Previous
                  </Button>
                  <Button
                    variant="outline"
                    className="border-orange-300 text-orange-700 hover:bg-orange-50"
                    size="sm"
                    disabled={!pagination.hasNextPage}
                    onClick={() => setCurrentPage(currentPage + 1)}
                  >
                    Next
                    <ChevronRight className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  )
}

