"use client"

import Link from "next/link"
import { usePathname } from "next/navigation"
import { useState } from "react"
import { 
  ShoppingCart, 
  Search, 
  Menu, 
  X, 
  User,
  Heart,
  ChevronDown,
  Home,
  Shield,
  Wifi,
  Camera
} from "lucide-react"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { useWishlistStore } from "@/store/wishlist"
import { useCart } from "@/hooks/use-cart"

const categories = [
  {
    name: "Smart Hubs",
    href: "/products/smart-hubs",
    subcategories: [
      { name: "MixPad 7 Series", href: "/products/smart-hubs?filter=mixpad-7" },
      { name: "MixPad M5", href: "/products/smart-hubs?filter=mixpad-m5" },
      { name: "MixPad X Ultra", href: "/products/smart-hubs?filter=mixpad-x" },
      { name: "All Control Panels", href: "/products/smart-hubs" },
    ],
  },
  {
    name: "Smart Switches",
    href: "/products/smart-switches",
    subcategories: [
      { name: "Touch Pro White", href: "/products/smart-switches?filter=white" },
      { name: "Touch Pro Black", href: "/products/smart-switches?filter=black" },
      { name: "1-Gang Switches", href: "/products/smart-switches?filter=1-gang" },
      { name: "2-Gang Switches", href: "/products/smart-switches?filter=2-gang" },
      { name: "3-Gang Switches", href: "/products/smart-switches?filter=3-gang" },
    ],
  },
  {
    name: "Security & Sensors",
    href: "/products/security-sensors",
    subcategories: [
      { name: "Smart Door Locks", href: "/products/security-sensors?filter=locks" },
      { name: "Motion Sensors", href: "/products/security-sensors?filter=motion" },
      { name: "Door Sensors", href: "/products/security-sensors?filter=door" },
      { name: "All Security", href: "/products/security-sensors" },
    ],
  },
  {
    name: "Smart Cameras",
    href: "/products/smart-cameras",
    subcategories: [
      { name: "Indoor Cameras", href: "/products/smart-cameras?filter=indoor" },
      { name: "Outdoor Cameras", href: "/products/smart-cameras?filter=outdoor" },
      { name: "2K Cameras", href: "/products/smart-cameras?filter=2k" },
      { name: "All Cameras", href: "/products/smart-cameras" },
    ],
  },
  {
    name: "Smart Curtains",
