import { notFound } from "next/navigation"
import { Metadata } from "next"
import { prisma } from "@/lib/prisma"
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import Link from "next/link"
import Image from "next/image"
import { Star, Package, ArrowLeft } from "lucide-react"
import { WishlistActions } from "@/components/wishlist-actions"
import { CartActions } from "@/components/cart-actions"
import { CategorySort } from "@/components/category-sort"

interface CategoryPageProps {
  params: Promise<{
    category: string
  }>
  searchParams: Promise<{
    page?: string
    sort?: string
  }>
}

export async function generateMetadata({ params }: { params: Promise<{ category: string }> }): Promise<Metadata> {
  const resolvedParams = await params
  const categoryData = await prisma.category.findUnique({
    where: { slug: resolvedParams.category },
    include: { parent: true }
  })

  if (!categoryData) {
    return {
      title: 'Category Not Found',
      description: 'The requested category could not be found.'
    }
  }

  const title = categoryData.parent 
    ? `${categoryData.name} - ${categoryData.parent.name} | Vsense Smart Home NZ`
    : `${categoryData.name} | Vsense Smart Home NZ`

  const description = categoryData.description || 
    `Shop ${categoryData.name} products at Vsense. Premium smart home solutions for New Zealand homes with fast shipping and expert support.`

  return {
    title,
    description,
    keywords: [
      categoryData.name,
      'smart home',
      'New Zealand',
      'Orvibo',
      'home automation',
      categoryData.parent?.name
    ].filter(Boolean).join(', '),
    openGraph: {
      title,
      description,
      type: 'website',
      locale: 'en_NZ'
    }
  }
}

export default async function CategoryPage({ 
  params,
  searchParams 
}: CategoryPageProps) {
  const { category } = await params
  const resolvedSearchParams = await searchParams
  const page = parseInt(resolvedSearchParams.page || "1")
  const sort = resolvedSearchParams.sort || "newest"
  const limit = 12

  // Find the category by slug
  const categoryData = await prisma.category.findUnique({
    where: { slug: category },
    include: {
      children: true,
      parent: true
    }
  })

  if (!categoryData) {
    notFound()
  }

  // Build where clause for products
  const whereClause = {
    status: "ACTIVE" as const,
    OR: [
      { categoryId: categoryData.id },
      // Include products from child categories if this is a parent category
      ...(categoryData.children.length > 0 
        ? [{ categoryId: { in: categoryData.children.map(child => child.id) } }]
        : []
      )
    ]
  }

  // Build sort clause
  let orderBy: any = { createdAt: 'desc' }
  switch (sort) {
    case 'oldest':
      orderBy = { createdAt: 'asc' }
      break
    case 'priceAsc':
      orderBy = { price: 'asc' }
      break
    case 'priceDesc':
      orderBy = { price: 'desc' }
      break
    case 'name':
      orderBy = { name: 'asc' }
      break
    default:
      orderBy = { createdAt: 'desc' }
  }

  // Get products and count
  const [products, totalProducts] = await Promise.all([
    prisma.product.findMany({
      where: whereClause,
      include: {
        images: {
          where: { isPrimary: true },
          take: 1
        },
        category: true,
        brand: true,
        _count: {
          select: { reviews: true }
        }
      },
      orderBy,
      take: limit,
      skip: (page - 1) * limit
    }),
    prisma.product.count({ where: whereClause })
  ])

  const totalPages = Math.ceil(totalProducts / limit)
  const hasNextPage = page < totalPages
  const hasPrevPage = page > 1

  // Generate page title and description
  const pageTitle = categoryData.parent 
    ? `${categoryData.name} - ${categoryData.parent.name}`
    : categoryData.name
  const pageDescription = categoryData.description || `Shop ${categoryData.name} products`

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-amber-50">
      <div className="container mx-auto py-8 px-4">
        <div className="max-w-7xl mx-auto">
        {/* Breadcrumb */}
        <nav className="flex items-center space-x-2 text-sm text-orange-600 mb-6">
          <Link href="/" className="hover:text-orange-800 transition-colors">Home</Link>
          <span>/</span>
          <Link href="/products" className="hover:text-orange-800 transition-colors">Products</Link>
          <span>/</span>
          {categoryData.parent && (
            <>
              <Link href={`/products/${categoryData.parent.slug}`} className="hover:text-orange-800 transition-colors">
                {categoryData.parent.name}
              </Link>
              <span>/</span>
            </>
          )}
          <span className="text-orange-900 font-medium">{categoryData.name}</span>
        </nav>

        {/* Enhanced Category Header */}
        <div className="mb-12">
          <div className="bg-gradient-to-br from-orange-100 to-amber-100 rounded-3xl p-8 mb-8 shadow-xl">
            <div className="flex flex-col md:flex-row items-start md:items-center gap-6">
              <Link href="/products">
                <Button variant="outline" size="lg" className="px-6 border-orange-300 text-orange-800 hover:bg-orange-50 rounded-full">
                  <ArrowLeft className="h-5 w-5 mr-2" />
                  All Products
                </Button>
              </Link>
              <div className="flex-1">
                <h1 className="text-4xl md:text-5xl font-bold text-orange-900 mb-3">{pageTitle}</h1>
                <div className="flex items-center gap-4 mb-4">
                  <div className="flex items-center gap-2">
                    <Package className="h-5 w-5 text-orange-600" />
                    <span className="text-lg font-semibold text-orange-600">
                      {totalProducts} product{totalProducts !== 1 ? 's' : ''}
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <div className="w-2 h-2 bg-green-500 rounded-full" />
                    <span className="text-sm text-orange-700">In Stock & Ready to Ship</span>
                  </div>
                </div>
                {categoryData.description && (
                  <p className="text-orange-700 text-lg leading-relaxed max-w-3xl">{categoryData.description}</p>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Child Categories (if any) */}
        {categoryData.children.length > 0 && (
          <div className="mb-8">
            <h2 className="text-lg font-bold text-orange-900 mb-4">Browse by Category</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-3">
              {categoryData.children.map((child) => (
                <Link key={child.id} href={`/products/${child.slug}`}>
                  <Card className="hover:shadow-xl transition-all duration-300 cursor-pointer border border-orange-100 rounded-2xl transform hover:-translate-y-1">
                    <CardContent className="p-4 text-center">
                      <div className="w-12 h-12 bg-gradient-to-br from-orange-100 to-amber-100 rounded-2xl flex items-center justify-center mx-auto mb-2 shadow-md">
                        <Package className="h-6 w-6 text-orange-600" />
                      </div>
                      <h3 className="font-medium text-sm text-orange-900">{child.name}</h3>
                    </CardContent>
                  </Card>
                </Link>
              ))}
            </div>
          </div>
        )}

        {/* Enhanced Sort and Filter Bar */}
        <div className="bg-white border border-orange-100 rounded-3xl p-6 mb-8 shadow-lg">
          <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
            <div className="flex items-center gap-4">
              <div className="text-sm font-medium text-orange-900">
                Showing {Math.min((page - 1) * limit + 1, totalProducts)}-{Math.min(page * limit, totalProducts)} of {totalProducts} products
              </div>
              <div className="hidden sm:block w-px h-6 bg-orange-200" />
              <div className="flex items-center gap-2 text-sm text-orange-700">
                <div className="w-2 h-2 bg-orange-500 rounded-full" />
                <span>Premium Quality</span>
              </div>
            </div>
            <CategorySort currentSort={sort} />
          </div>
        </div>

        {/* Products Grid */}
        {products.length === 0 ? (
          <Card>
            <CardContent className="text-center py-12">
              <Package className="h-16 w-16 mx-auto text-gray-400 mb-4" />
              <h3 className="text-lg font-medium text-orange-900 mb-2">No products found</h3>
              <p className="text-orange-600 mb-6">
                We don't have any products in this category yet. Check back soon!
              </p>
              <Link href="/products">
                <Button className="bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600 rounded-full">
                  Browse All Products
                </Button>
              </Link>
            </CardContent>
          </Card>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
              {products.map((product) => (
                <Card key={product.id} className="group hover:shadow-xl transition-all duration-300 hover:scale-105 border-0 shadow-md overflow-hidden hover:-translate-y-2 h-full flex flex-col">
                  <div className="relative">
                    <div className="aspect-square relative bg-gradient-to-br from-slate-50 to-slate-100 overflow-hidden">
                      {product.images[0] ? (
                        <Image
                          src={product.images[0].url}
                          alt={product.images[0].alt || product.name}
                          fill
                          className="object-cover group-hover:scale-105 transition-transform duration-300"
                        />
                      ) : (
                        <div className="w-full h-full flex items-center justify-center">
                          <Package className="h-16 w-16 text-gray-400" />
                        </div>
                      )}

                      {/* Stock Status */}
                      <div className="absolute top-3 left-3">
                        <Badge variant={product.stockQuantity > 0 ? "default" : "destructive"}>
                          {product.stockQuantity > 0 ? "In Stock" : "Out of Stock"}
                        </Badge>
                      </div>

                      {/* Discount Badge */}
                      {product.comparePrice && product.comparePrice > product.price && (
                        <div className="absolute top-3 right-3">
                          <Badge variant="destructive">
                            {Math.round(((product.comparePrice - product.price) / product.comparePrice) * 100)}% OFF
                          </Badge>
                        </div>
                      )}
                    </div>

                    <CardContent className="p-4 flex-1 flex flex-col">
                      <div className="space-y-4 flex-1 flex flex-col">
                        {/* Product Info */}
                        <div>
                          <Link href={`/product/${product.slug}`} className="group-hover:text-orange-600 transition-colors duration-300">
                            <h3 className="font-semibold text-orange-900 group-hover:text-orange-600 transition-colors duration-300 line-clamp-2 text-lg leading-tight">
                              {product.name}
                            </h3>
                          </Link>
                          <div className="flex items-center gap-2 mt-2">
                            <span className="bg-orange-100 text-orange-700 px-2 py-1 rounded-full text-xs font-medium">
                              {product.brand?.name}
                            </span>
                            <span className="text-orange-400">•</span>
                            <span className="text-sm text-orange-600">{product.category.name}</span>
                          </div>
                        </div>

                        {/* Reviews */}
                        <div className="flex items-center gap-1">
                          <div className="flex">
                            {[...Array(5)].map((_, i) => (
                              <Star
                                key={i}
                                className="h-4 w-4 text-orange-300"
                              />
                            ))}
                          </div>
                          <span className="text-sm text-orange-600">
                            ({product._count.reviews})
                          </span>
                        </div>

                        {/* Flexible spacer */}
                        <div className="flex-1"></div>

                        {/* Price and Stock at bottom */}
                        <div className="mt-auto space-y-3">
                          {/* Stock Warning */}
                          {product.stockQuantity > 0 && product.stockQuantity <= 5 && (
                            <p className="text-sm text-orange-600">
                              Only {product.stockQuantity} left in stock
                            </p>
                          )}

                          {/* Price */}
                          <div className="bg-orange-50 rounded-lg p-3">
                            <div className="flex items-center gap-3">
                              <span className="text-xl font-bold text-orange-600">
                                NZ${product.price.toFixed(2)}
                              </span>
                              {product.comparePrice && product.comparePrice > product.price && (
                                <>
                                  <span className="text-sm text-orange-500 line-through">
                                    NZ${product.comparePrice.toFixed(2)}
                                  </span>
                                  <span className="bg-red-500 text-white px-2 py-1 rounded text-xs font-semibold">
                                    SAVE
                                  </span>
                                </>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    </CardContent>

                    <CardFooter className="p-4 pt-0 mt-auto">
                      {/* 三个按钮水平排列，顶部对齐，相同间隔 */}
                      <div className="flex items-start gap-3 w-full">
                        {/* Add to Cart 按钮 */}
                        <CartActions 
                          productId={product.id}
                          stockQuantity={product.stockQuantity}
                          className="flex-1 h-11 bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600 text-white shadow-md font-semibold transition-all duration-300 hover:scale-105 hover:shadow-xl hover:shadow-orange-500/30 cursor-pointer text-sm"
                        />

                        {/* Like 按钮 */}
                        <div className="flex-shrink-0">
                          <WishlistActions productId={product.id} compact={true} />
