                        {/* View Details 按钮 */}
                        <Link href={`/product/${product.slug}`} className="flex-1 cursor-pointer">
                          <Button variant="outline" className="w-full h-11 text-sm border-orange-200 text-orange-700 hover:bg-orange-50 hover:border-orange-300 transition-all duration-300 hover:scale-105 hover:shadow-md hover:shadow-orange-200/50 cursor-pointer">
                            View Details
                          </Button>
                        </Link>
                      </div>
                    </CardFooter>
                  </div>
                </Card>
              ))}
            </div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="flex items-center justify-between mt-8">
                <div className="text-sm text-gray-600">
                  Page {page} of {totalPages}
                </div>
                <div className="flex items-center gap-2">
                  {hasPrevPage && (
                    <Link href={`/products/${category}?page=${page - 1}${sort !== 'newest' ? `&sort=${sort}` : ''}`}>
                      <Button variant="outline" size="sm">
                        Previous
                      </Button>
                    </Link>
                  )}
                  {hasNextPage && (
                    <Link href={`/products/${category}?page=${page + 1}${sort !== 'newest' ? `&sort=${sort}` : ''}`}>
                      <Button variant="outline" size="sm">
                        Next
                      </Button>
                    </Link>
                  )}
                </div>
              </div>
            )}
          </>
        )}

        {/* Related Categories */}
        {categoryData.parent && (
          <div className="mt-12 pt-8 border-t">
            <h2 className="text-lg font-semibold mb-4">Related Categories</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {categoryData.parent.children
                .filter(sibling => sibling.id !== categoryData.id)
                .slice(0, 4)
                .map((sibling) => (
                  <Link key={sibling.id} href={`/products/${sibling.slug}`}>
                    <Card className="hover:shadow-md transition-shadow cursor-pointer">
                      <CardContent className="p-4 text-center">
                        <div className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center mx-auto mb-2">
                          <Package className="h-5 w-5 text-gray-600" />
                        </div>
                        <h3 className="font-medium text-sm">{sibling.name}</h3>
                      </CardContent>
                    </Card>
                  </Link>
                ))}
            </div>
          </div>
        )}
        </div>
      </div>
    </div>
  )
}

