        className="absolute left-4 top-1/2 -translate-y-1/2 z-10 bg-black/20 hover:bg-orange-500/80 text-white rounded-full p-3 transition-all duration-300 hover:scale-110 hover:-translate-x-1 shadow-lg hover:shadow-orange-500/50 cursor-pointer"
        aria-label="Previous slide"
      >
        <ChevronLeft className="h-6 w-6 transition-transform duration-300" />
      </button>

      <button
        onClick={nextSlide}
        className="absolute right-4 top-1/2 -translate-y-1/2 z-10 bg-black/20 hover:bg-orange-500/80 text-white rounded-full p-3 transition-all duration-300 hover:scale-110 hover:translate-x-1 shadow-lg hover:shadow-orange-500/50 cursor-pointer"
        aria-label="Next slide"
      >
        <ChevronRight className="h-6 w-6 transition-transform duration-300" />
      </button>

      {/* Enhanced Dot Indicators */}
      <div className="absolute bottom-6 left-1/2 -translate-x-1/2 flex gap-4 z-10">
        {carouselSlides.map((_, index) => (
          <button
            key={index}
            onClick={() => goToSlide(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 hover:scale-125 cursor-pointer ${
              index === currentSlide 
                ? 'bg-orange-400 shadow-lg shadow-orange-400/50 w-8' 
                : 'bg-white/40 hover:bg-orange-300/80 hover:shadow-md hover:shadow-orange-300/30'
            }`}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  )
}

