import { NextRequest, NextResponse } from "next/server"
import { prisma } from "@/lib/prisma"
import { z } from "zod"
import { rateLimiters, withRateLimit } from "@/lib/rate-limit"

const productsQuerySchema = z.object({
  page: z.string().optional().default("1"),
  limit: z.string().optional().default("12"),
  category: z.string().optional(),
  minPrice: z.string().optional(),
  maxPrice: z.string().optional(),
  search: z.string().optional(),
  sort: z.enum(["newest", "oldest", "priceAsc", "priceDesc", "name"]).optional().default("newest"),
  featured: z.string().optional(),
})

export async function GET(request: NextRequest) {
  return withRateLimit(request, rateLimiters.api, async () => {
  try {
    const searchParams = request.nextUrl.searchParams
    const query = productsQuerySchema.parse({
      page: searchParams.get("page") || "1",
      limit: searchParams.get("limit") || "12", 
      category: searchParams.get("category") || undefined,
      minPrice: searchParams.get("minPrice") || undefined,
      maxPrice: searchParams.get("maxPrice") || undefined,
      search: searchParams.get("search") || undefined,
      sort: searchParams.get("sort") || "newest",
      featured: searchParams.get("featured") || undefined,
    })

    const page = parseInt(query.page)
    const limit = Math.min(parseInt(query.limit), 50) // Cap at 50 items per page
    const skip = (page - 1) * limit

    // Build the where clause
    const where: any = {
      status: "ACTIVE", // Only show active products
    }

    // Category filter
    if (query.category) {
      where.category = {
        slug: query.category
      }
    }

    // Price range filter
    if (query.minPrice || query.maxPrice) {
      where.price = {}
      if (query.minPrice) {
        where.price.gte = parseFloat(query.minPrice)
      }
      if (query.maxPrice) {
        where.price.lte = parseFloat(query.maxPrice)
      }
    }

    // Search filter
    if (query.search) {
      where.OR = [
        {
          name: {
            contains: query.search,
            mode: "insensitive"
          }
        },
        {
          description: {
            contains: query.search,
            mode: "insensitive"
          }
        },
        {
          tags: {
            hasSome: [query.search]
          }
        }
      ]
    }

    // Featured filter
    if (query.featured === "true") {
      where.featured = true
    }

    // Build the orderBy clause
    let orderBy: any = { createdAt: "desc" } // default to newest

    switch (query.sort) {
      case "oldest":
        orderBy = { createdAt: "asc" }
        break
      case "priceAsc":
        orderBy = { price: "asc" }
        break
      case "priceDesc":
        orderBy = { price: "desc" }
        break
      case "name":

