"use client"

import { useState, useEffect } from "react"
import { useSession } from "next-auth/react"
import Link from "next/link"
import Image from "next/image"
import { Button } from "@/components/ui/button"
import { Card } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import {
  Minus,
  Plus,
  Trash2,
  ShoppingBag,
  ArrowRight,
  Truck,
  Shield,
  CreditCard,
  Loader2,
} from "lucide-react"
import { formatPrice, calculateGST, getShippingCost } from "@/lib/utils"

interface CartItem {
  id: string
  productId: string
  quantity: number
  product: {
    id: string
    name: string
    price: number
    images: Array<{ url: string; alt?: string }>
  }
}

interface Cart {
  id: string
  items: CartItem[]
}

export default function CartPage() {
  const { data: session } = useSession()
  const [cart, setCart] = useState<Cart | null>(null)
  const [loading, setLoading] = useState(true)
  const [updating, setUpdating] = useState<string | null>(null)

  const fetchCart = async () => {
    try {
      setLoading(true)
      const response = await fetch('/api/cart')
      const data = await response.json()

      if (data.success) {
        setCart(data.cart)
      }
    } catch (error) {
      console.error('Failed to fetch cart:', error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchCart()
  }, [])

  const updateQuantity = async (itemId: string, newQuantity: number) => {
    if (newQuantity < 1) return

    setUpdating(itemId)
    try {
      const response = await fetch(`/api/cart/${itemId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ quantity: newQuantity })
      })

      if (response.ok) {
        await fetchCart()
        // Trigger cart update event
        window.dispatchEvent(new Event('cart-updated'))
      }
    } catch (error) {
      console.error('Failed to update quantity:', error)
    } finally {
      setUpdating(null)
    }
  }

  const removeItem = async (itemId: string) => {
    setUpdating(itemId)
    try {
      const response = await fetch(`/api/cart/${itemId}`, {
        method: 'DELETE'
      })

      if (response.ok) {
        await fetchCart()
        // Trigger cart update event
        window.dispatchEvent(new Event('cart-updated'))
      }
    } catch (error) {
      console.error('Failed to remove item:', error)
    } finally {
      setUpdating(null)
    }
  }

  const clearCart = async () => {
    try {
      const response = await fetch('/api/cart', {
        method: 'DELETE'
      })

      if (response.ok) {
        await fetchCart()
        // Trigger cart update event
        window.dispatchEvent(new Event('cart-updated'))
      }
    } catch (error) {
      console.error('Failed to clear cart:', error)
    }
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-amber-50 flex items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin text-orange-600" />
      </div>
    )
  }

  const items = cart?.items || []
  const subtotal = items.reduce((sum, item) => sum + (item.product.price * item.quantity), 0)
  const shipping = getShippingCost(subtotal)
  const gst = calculateGST(subtotal + shipping)
  const total = subtotal + shipping

  if (items.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-amber-50">
        <div className="container mx-auto px-4 py-16">
          <div className="max-w-md mx-auto text-center">
            <ShoppingBag className="h-24 w-24 text-orange-300 mx-auto mb-6" />
            <h1 className="text-2xl font-bold mb-4 text-orange-900">Your cart is empty</h1>
            <p className="text-orange-600 mb-8">
              Looks like you haven&apos;t added anything to your cart yet.
            </p>
            <Link href="/products">
              <Button size="lg" className="bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600">
                Continue Shopping
                <ArrowRight className="ml-2 h-5 w-5" />
              </Button>
            </Link>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8 text-orange-900">Shopping Cart</h1>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Cart Items */}
          <div className="lg:col-span-2">
            <Card className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold text-orange-900">
                  Cart Items ({items.length})
                </h2>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={clearCart}
                  className="text-red-600 hover:text-red-700"
                >
                  Clear All
                </Button>
              </div>

              <div className="space-y-4">
                {items.map((item) => (
                  <div
                    key={item.id}
                    className="flex gap-4 py-4 border-b last:border-0"
                  >
                    <div className="w-24 h-24 bg-gradient-to-br from-orange-100 to-amber-100 rounded-md overflow-hidden">
                      {item.product.images[0] ? (
                        <Image
                          src={item.product.images[0].url}
                          alt={item.product.images[0].alt || item.product.name}
                          width={96}
                          height={96}
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        <div className="w-full h-full flex items-center justify-center text-orange-400">
                          [No Image]
                        </div>
                      )}
                    </div>

                    <div className="flex-1">
                      <h3 className="font-semibold mb-1 text-orange-900">{item.product.name}</h3>
                      <p className="text-lg font-bold text-orange-600">
                        {formatPrice(item.product.price)}
                      </p>
                    </div>

                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="icon"
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                        disabled={item.quantity === 1 || updating === item.id}
                      >
                        <Minus className="h-4 w-4" />
                      </Button>

                      <Input
                        type="number"
                        value={item.quantity}
                        onChange={(e) =>
                          updateQuantity(item.id, parseInt(e.target.value) || 1)
                        }
                        className="w-16 text-center"
                        min="1"
                        disabled={updating === item.id}
                      />

                      <Button
                        variant="outline"
                        size="icon"
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                        disabled={updating === item.id}
                      >
                        <Plus className="h-4 w-4" />
                      </Button>
                    </div>

                    <div className="flex items-center gap-4">
                      <p className="font-semibold text-orange-900">
                        {formatPrice(item.product.price * item.quantity)}
                      </p>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => removeItem(item.id)}
                        className="text-red-600 hover:text-red-700"
                        disabled={updating === item.id}
                      >
                        {updating === item.id ? (
                          <Loader2 className="h-5 w-5 animate-spin" />
                        ) : (
                          <Trash2 className="h-5 w-5" />
                        )}
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </Card>

            {/* Features */}
            <div className="grid md:grid-cols-3 gap-4 mt-6">
              <div className="flex items-center gap-3 p-4 bg-white rounded-lg">
                <Truck className="h-8 w-8 text-orange-600" />
                <div>
                  <p className="font-semibold text-orange-900">Free Shipping</p>
                  <p className="text-sm text-orange-600">Orders over NZ$99</p>
                </div>
              </div>
              <div className="flex items-center gap-3 p-4 bg-white rounded-lg">
                <Shield className="h-8 w-8 text-amber-600" />
                <div>
                  <p className="font-semibold text-orange-900">Secure Checkout</p>
                  <p className="text-sm text-orange-600">SSL Encrypted</p>
                </div>
              </div>
              <div className="flex items-center gap-3 p-4 bg-white rounded-lg">
                <CreditCard className="h-8 w-8 text-red-600" />
                <div>
                  <p className="font-semibold text-orange-900">Payment Options</p>
                  <p className="text-sm text-orange-600">Cards, Afterpay</p>
                </div>
              </div>
            </div>
          </div>

          {/* Order Summary */}
          <div className="lg:col-span-1">
            <Card className="p-6 sticky top-20">
              <h2 className="text-xl font-semibold mb-6 text-orange-900">Order Summary</h2>

              <div className="space-y-3 mb-6">
                <div className="flex justify-between">
                  <span>Subtotal</span>
                  <span>{formatPrice(subtotal)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Shipping</span>
                  <span>
                    {shipping === 0 ? (
                      <span className="text-orange-600">FREE</span>
                    ) : (
                      formatPrice(shipping)
                    )}
                  </span>
                </div>
                <div className="flex justify-between text-sm text-orange-600">
                  <span>GST (included)</span>
                  <span>{formatPrice(gst)}</span>
                </div>

                {subtotal < 99 && (
                  <div className="bg-orange-50 p-3 rounded-md">
                    <p className="text-sm text-orange-800">
                      Add {formatPrice(99 - subtotal)} more for free shipping!
                    </p>
                  </div>
                )}

                <div className="border-t pt-3">
                  <div className="flex justify-between text-lg font-bold text-orange-900">
                    <span>Total</span>
                    <span>{formatPrice(total)}</span>
                  </div>
                  <p className="text-xs text-orange-600 mt-1">
                    (incl. GST)
                  </p>
                </div>
              </div>

              {/* Promo Code */}
              <div className="mb-6">
                <label className="block text-sm font-medium mb-2 text-orange-900">
                  Promo Code
                </label>
                <div className="flex gap-2">
                  <Input placeholder="Enter code" />
                  <Button variant="outline" className="border-orange-300 text-orange-700 hover:bg-orange-50">Apply</Button>
                </div>
              </div>

              <Link href="/checkout">
                <Button className="w-full mb-3 bg-gradient-to-r from-orange-500 to-amber-500 hover:from-orange-600 hover:to-amber-600" size="lg">
                  Proceed to Checkout
                  <ArrowRight className="ml-2 h-5 w-5" />
                </Button>
              </Link>

              <Link href="/products">
                <Button variant="outline" className="w-full border-orange-300 text-orange-700 hover:bg-orange-50">
                  Continue Shopping
                </Button>
              </Link>

              {/* Payment Methods */}
              <div className="mt-6 pt-6 border-t">
                <p className="text-sm text-orange-600 mb-3">
                  Accepted Payment Methods:
                </p>
                <div className="flex gap-2 flex-wrap">
                  <div className="h-8 w-12 bg-orange-100 rounded flex items-center justify-center text-xs text-orange-700">
                    Visa
                  </div>
                  <div className="h-8 w-12 bg-orange-100 rounded flex items-center justify-center text-xs text-orange-700">
                    MC
                  </div>
                  <div className="h-8 w-12 bg-orange-100 rounded flex items-center justify-center text-xs text-orange-700">
                    Amex
                  </div>
                  <div className="h-8 w-16 bg-orange-100 rounded flex items-center justify-center text-xs text-orange-700">
                    PayPal
                  </div>
                  <div className="h-8 w-20 bg-orange-100 rounded flex items-center justify-center text-xs text-orange-700">
                    Afterpay
                  </div>
                </div>
              </div>
            </Card>
          </div>
        </div>
      </div>
    </div>
  )
}

