import { NextRequest } from 'next/server';

// In-memory rate limiting for development (not suitable for production with multiple instances)
const rateLimitStore = new Map<string, { count: number; resetTime: number }>();

export interface RateLimitConfig {
  windowMs: number; // Time window in milliseconds
  maxRequests: number; // Maximum requests per window
  keyGenerator?: (request: NextRequest) => string; // Custom key generator
  skipSuccessfulRequests?: boolean; // Skip successful requests from count
  skipFailedRequests?: boolean; // Skip failed requests from count
}

export interface RateLimitResult {
  success: boolean;
  limit: number;
  remaining: number;
  reset: Date;
  retryAfter?: number;
}

/**
 * Simple in-memory rate limiter
 * For production, consider using Redis-based rate limiting
 */
export function createRateLimit(config: RateLimitConfig) {
  const {
    windowMs,
    maxRequests,
    keyGenerator = (request) => getDefaultKey(request),
  } = config;

  return async function rateLimit(request: NextRequest): Promise<RateLimitResult> {
    const key = keyGenerator(request);
    const now = Date.now();
    const windowStart = now - windowMs;

    // Clean up expired entries
    if (Math.random() < 0.1) { // Clean up 10% of the time to avoid constant cleanup
      cleanupExpiredEntries(windowStart);
    }

    let record = rateLimitStore.get(key);

    if (!record || record.resetTime <= now) {
      // Create new record or reset expired record
      record = {
        count: 1,
        resetTime: now + windowMs,
      };
      rateLimitStore.set(key, record);

      return {
        success: true,
        limit: maxRequests,
        remaining: maxRequests - 1,
        reset: new Date(record.resetTime),
      };
    }

    if (record.count >= maxRequests) {
      // Rate limit exceeded
      return {
        success: false,
        limit: maxRequests,
        remaining: 0,
        reset: new Date(record.resetTime),
        retryAfter: Math.ceil((record.resetTime - now) / 1000),
      };
    }

    // Increment count
    record.count += 1;

    return {
      success: true,
      limit: maxRequests,
      remaining: maxRequests - record.count,
      reset: new Date(record.resetTime),
    };
  };
}

function getDefaultKey(request: NextRequest): string {
  // Try to get IP from various headers (for different deployment scenarios)
  const forwarded = request.headers.get('x-forwarded-for');
  const realIp = request.headers.get('x-real-ip');
  const cfConnectingIp = request.headers.get('cf-connecting-ip');

  const ip = forwarded?.split(',')[0]?.trim() || 
             realIp || 
             cfConnectingIp || 
             'unknown';

  return `ip:${ip}`;
}

function cleanupExpiredEntries(windowStart: number) {
  const now = Date.now();
  for (const [key, record] of rateLimitStore.entries()) {
    if (record.resetTime <= now) {
      rateLimitStore.delete(key);
    }
  }
}

// Predefined rate limiters for common use cases
export const rateLimiters = {
  // Strict rate limiting for authentication endpoints
  auth: createRateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 5, // 5 attempts per 15 minutes
  }),

  // API rate limiting
  api: createRateLimit({
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 60, // 60 requests per minute
  }),

  // Contact/form submissions
  form: createRateLimit({
    windowMs: 10 * 60 * 1000, // 10 minutes
    maxRequests: 3, // 3 submissions per 10 minutes
  }),

  // General purpose
  general: createRateLimit({
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 100, // 100 requests per minute
  }),
};

// Middleware helper
export async function withRateLimit<T>(
  request: NextRequest,
  rateLimit: (req: NextRequest) => Promise<RateLimitResult>,
  handler: () => Promise<T>
): Promise<Response | T> {
  const result = await rateLimit(request);

  if (!result.success) {
    return Response.json(
      {
        error: 'Too Many Requests',
        message: 'Rate limit exceeded. Please try again later.',
        retryAfter: result.retryAfter,
      },
      {
        status: 429,
        headers: {
          'Retry-After': result.retryAfter?.toString() || '60',
          'X-RateLimit-Limit': result.limit.toString(),
          'X-RateLimit-Remaining': result.remaining.toString(),
          'X-RateLimit-Reset': result.reset.toISOString(),
        },
      }
    );
  }

  const response = await handler();

  // Add rate limit headers to successful responses
  if (response instanceof Response) {
    response.headers.set('X-RateLimit-Limit', result.limit.toString());
    response.headers.set('X-RateLimit-Remaining', result.remaining.toString());
    response.headers.set('X-RateLimit-Reset', result.reset.toISOString());
  }

  return response;
}

import { PrismaClient } from "@prisma/client"

declare global {
  var prisma: PrismaClient | undefined
}

export const prisma = globalThis.prisma || new PrismaClient()

if (process.env.NODE_ENV !== "production") globalThis.prisma = prisma

